import React, { useEffect, useState, useContext } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles/ProductDetails.css";

const ProductDetail = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    if (!id) {
      console.error("No product ID found in URL");
      return;
    }

    axios
      .get(`http://localhost:8080/api/products/${id}`)
      .then((response) => {
        if (response.data) {
          setProduct(response.data);
        } else {
          setError("Product not found.");
        }
      })
      .catch(() => {
        setError("Failed to load product details. Please try again.");
      });
  }, [id]);

  const buyNow = () => {
    navigate(`/payment?productId=${id}&price=${product.price}`);
  };

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  if (!product) {
    return <div className="loading">Loading product details...</div>;
  }

  return (
    <div className="product-detail">
      <div className="product-container">
        <div className="image-container">
          <img
            src={product.productUrl || "https://via.placeholder.com/300"}
            alt={product.productName}
            className="product-image"
          />
        </div>
        <div className="product-info">
          <h1 className="product-name">{product.productName}</h1>
          <p className="product-category">
            Category: <span>{product.category?.categoryName || "Uncategorized"}</span>
          </p>
          <p className="product-price">â‚¹{parseFloat(product.price).toFixed(2)}</p>
          <p className="product-description">{product.description}</p>
          <button className="buy-now-btn" onClick={buyNow}>Buy Now</button>
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
