import React, { useEffect, useState, useContext } from "react";
import { Link } from "react-router-dom";
import "../../styles/dashboard.css";
import { ProductContext } from "../../context/ProductContext";

const Dashboard = () => {
  const { products, setProducts } = useContext(ProductContext);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [useSuperCoins, setUseSuperCoins] = useState({});

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await fetch(`http://localhost:8080/users/{userId}`);
        const data = await response.json();
        setUser(data);
      } catch (error) {
        console.error("Error fetching user:", error);
      }
    };

    const fetchProducts = async () => {
      try {
        const response = await fetch("http://localhost:8080/api/products");
        const data = await response.json();
        setProducts(data);
      } catch (error) {
        console.error("Error fetching products:", error);
      }
      setLoading(false);
    };

    fetchUser();
    fetchProducts();
  }, [setProducts]);

  const toggleSuperCoins = (productId) => {
    setUseSuperCoins((prevState) => ({
      ...prevState,
      [productId]: !prevState[productId],
    }));
  };

  const calculateFinalPrice = (product) => {
    let finalPrice = product.price;
    if (user?.isMember) {
      finalPrice *= 0.9; // 10% discount for members
    }
    return finalPrice;
  };

  const calculateSuperCoinDiscount = (productId, product) => {
    const finalPrice = calculateFinalPrice(product);
    const superCoinValue = user?.superCoins ? Math.min(user.superCoins / 10, finalPrice) : 0;
    return useSuperCoins[productId] ? (finalPrice - superCoinValue).toFixed(2) : finalPrice.toFixed(2);
  };

  return (
    <div className="dashboard">
      <h2>Welcome to eMart Dashboard</h2>
      {loading ? (
        <p>Loading products...</p>
      ) : (
        <div className="product-grid">
          {products.map((product) => {
            const discountedPrice = calculateSuperCoinDiscount(product.id, product);
            return (
              <div key={product.id} className="product-card">
                <Link to={`/product/${product.id}`}>
                  <img src={product.imageUrl} alt={product.name} className="product-image" />
                  <h3>{product.name}</h3>
                </Link>
                <p>Price: ₹{product.price.toFixed(2)}</p>
                {user?.isMember && (
                  <p className="discounted-price">Member Price: ₹{calculateFinalPrice(product).toFixed(2)}</p>
                )}
                {user?.superCoins && (
                  <>
                    <label>
                      <input
                        type="checkbox"
                        checked={useSuperCoins[product.id] || false}
                        onChange={() => toggleSuperCoins(product.id)}
                      />
                      Use SuperCoins
                    </label>
                    <p className="supercoin-price">Final Price: ₹{discountedPrice}</p>
                  </>
                )}
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default Dashboard;
