import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/Cart.css"; // Import CSS for styling

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const userId = 1; // Change this dynamically based on the logged-in user

  useEffect(() => {
    fetchCartItems();
  }, []);

  // Fetch Cart Items from Backend
  const fetchCartItems = async () => {
    try {
      const response = await axios.get(`http://localhost:8080/api/cart/user/${userId}`);
      setCartItems(response.data);
    } catch (error) {
      console.error("Error fetching cart items:", error);
    }
  };

  // Update Cart Item Quantity
  const updateQuantity = async (cartId, newQuantity) => {
    if (newQuantity < 1) return; // Prevent quantity from being less than 1

    try {
      await axios.put(`http://localhost:8080/api/cart/update/${cartId}`, null, {
        params: { quantity: newQuantity },
      });
      fetchCartItems(); // Refresh cart after update
    } catch (error) {
      console.error("Error updating cart item:", error);
    }
  };

  // Remove Item from Cart
  const removeFromCart = async (cartId) => {
    try {
      await axios.delete(`http://localhost:8080/api/cart/remove/${cartId}`);
      fetchCartItems(); // Refresh cart after removal
    } catch (error) {
      console.error("Error removing item from cart:", error);
    }
  };

  // Calculate Total Price
  const calculateTotalPrice = () => {
    return cartItems.reduce((total, item) => total + item.product.price * item.quantity, 0);
  };

  return (
    <div className="cart-container">
      <h2>Shopping Cart ðŸ›’</h2>
      {cartItems.length === 0 ? (
        <p className="empty-cart">Your cart is empty.</p>
      ) : (
        <div>
          {cartItems.map((item) => (
            <div key={item.id} className="cart-item">
              <img src={item.product.imageUrl} alt={item.product.name} className="cart-image" />
              <div className="cart-details">
                <h4>{item.product.name}</h4>
                <p>Price: â‚¹{item.product.price}</p>
                <div className="quantity-controls">
                  <button onClick={() => updateQuantity(item.id, item.quantity - 1)}>-</button>
                  <span>{item.quantity}</span>
                  <button onClick={() => updateQuantity(item.id, item.quantity + 1)}>+</button>
                </div>
                <p className="item-total">Total: â‚¹{item.product.price * item.quantity}</p>
                <button onClick={() => removeFromCart(item.id)} className="btn-remove">Remove</button>
              </div>
            </div>
          ))}
          <h3 className="total-price">Total Price: â‚¹{calculateTotalPrice()}</h3>
        </div>
      )}
    </div>
  );
};

export default Cart;
