import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles/ProductList.css";
import AuthContext from "../AuthContext";

const ProductList = () => {
    const [products, setProducts] = useState([]);
    const { user } = useContext(AuthContext);
    const userId = user?.userId;
    const [userData, setUserData] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        if (userId) {
            fetchUserData();
            fetchProducts();
        }
    }, [userId]);

    // Fetch user details
    const fetchUserData = async () => {
        try {
            const response = await axios.get(`http://localhost:8080/api/users/${userId}`);
            console.log("User Data:", response.data);
            setUserData(response.data);
        } catch (error) {
            console.error("Error fetching user data:", error);
        }
    };

    // Fetch all products
    const fetchProducts = async () => {
        try {
            const response = await axios.get("http://localhost:8080/api/products/$");
            console.log("Products:", response.data);
            setProducts(Array.isArray(response.data) ? response.data : []);
        } catch (error) {
            console.error("Error fetching products:", error);
            setProducts([]);
        }
    };

    // Calculate discounted price for premium members
    const getDiscountedPrice = (price) => {
        return userData?.member ? (price * 0.9).toFixed(2) : price.toFixed(2);
    };

    return (
        <div className="product-container">
            <h2>Product List</h2>

            <div className="product-grid">
                {products.length > 0 ? (
                    products.map((product) => {
                        const originalPrice = product.price;
                        const discountedPrice = getDiscountedPrice(originalPrice);
                        const isPremium = userData?.member;

                        return (
                            <div className="product-card" key={product.productId}>
                                <img
                                    src={product.productUrl || "https://via.placeholder.com/150"}
                                    alt={product.name}
                                    className="product-image"
                                />
                                <div className="product-info">
                                    <h3>{product.name}</h3>

                                    {/* Show price with discount for premium members */}
                                    {isPremium ? (
                                        <p className="product-price">
                                            <span className="original-price">₹{originalPrice}</span> ₹{discountedPrice}
                                        </p>
                                    ) : (
                                        <p className="product-price">₹{originalPrice}</p>
                                    )}

                                    <div className="product-buttons">
                                        <button className="buy-now" onClick={() => navigate(`/product/${product.productId}`)}>
                                            Buy Now
                                        </button>
                                    </div>
                                </div>
                            </div>
                        );
                    })
                ) : (
                    <p className="no-products">No products found</p>
                )}
            </div>
        </div>
    );
};

export default ProductList;
