import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { Container, Row, Col, Table, Button, Card } from 'react-bootstrap';
import { FaDownload } from 'react-icons/fa';
import { toast } from 'react-toastify';
import jsPDF from 'jspdf';
import 'react-toastify/dist/ReactToastify.css';
import '../styles/InvoicePage.css';

const InvoicePage = () => {
  const { paymentId } = useParams();
  const [paymentDetails, setPaymentDetails] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost:8081/api/paymentdetails/${paymentId}`)
      .then(response => setPaymentDetails(response.data))
      .catch(error => {
        console.error('Error fetching payment details:', error);
        toast.error("Failed to fetch payment details");
      });
  }, [paymentId]);

  if (!paymentDetails) return <div>Loading...</div>;

  const { order, paymentMethod, paymentStatus } = paymentDetails;
  const { user, orderDate, totalAmount, orderDetails } = order;
  const { name, mobileNumber, email, address } = user;

  const downloadInvoicePDF = () => {
    const doc = new jsPDF();
    doc.setFont("helvetica", "normal");
    doc.setFontSize(16);
    doc.text("Invoice", 20, 20);

    doc.setFontSize(12);
    doc.text(`Name: ${name}`, 20, 30);
    doc.text(`Email: ${email}`, 20, 40);
    doc.text(`Phone: ${mobileNumber}`, 20, 50);
    doc.text(`Address: ${address}`, 20, 60);

    doc.text(`Order ID: ${order.orderId}`, 150, 30);
    doc.text(`Order Date: ${new Date(orderDate).toLocaleDateString()}`, 150, 40);
    doc.text(`Payment Method: ${paymentMethod}`, 150, 50);
    doc.text(`Payment Status: ${paymentStatus}`, 150, 60);
    doc.text(`Total Amount: $${totalAmount.toFixed(2)}`, 150, 70);

    let yPosition = 80;
    doc.text("Product Details:", 20, yPosition);
    yPosition += 10;

    orderDetails.forEach(item => {
      doc.text(`- ${item.product.productName} | Quantity: ${item.quantity} | Price: $${item.price.toFixed(2)} | Total: $${(item.quantity * item.price).toFixed(2)}`, 20, yPosition);
      yPosition += 10;
    });

    doc.save("invoice.pdf");
    toast.success("Invoice downloaded successfully!");
  };

  return (
    <Container className="mt-5 animated fadeIn">
      <h2 className="mb-4">Invoice</h2>

      <Row className="mb-4">
        <Col md={6}>
          <Card className="p-3 shadow-lg">
            <h5>Customer Information</h5>
            <p><strong>Name:</strong> {name}</p>
            <p><strong>Email:</strong> {email}</p>
            <p><strong>Phone:</strong> {mobileNumber}</p>
            <p><strong>Address:</strong> {address}</p>
          </Card>
        </Col>
        <Col md={6}>
          <Card className="p-3 shadow-lg">
            <h5>Order Information</h5>
            <p><strong>Order ID:</strong> {order.orderId}</p>
            <p><strong>Order Date:</strong> {new Date(orderDate).toLocaleDateString()}</p>
            <p><strong>Status:</strong> {paymentStatus}</p>
            <p><strong>Payment Method:</strong> {paymentMethod}</p>
            <p><strong>Total Amount:</strong> ${totalAmount.toFixed(2)}</p>
          </Card>
        </Col>
      </Row>

      <Table striped bordered hover responsive>
        <thead>
          <tr>
            <th>Product</th>
            <th>Quantity</th>
            <th>Price</th>
            <th>Total</th>
          </tr>
        </thead>
        <tbody>
          {orderDetails.map(item => (
            <tr key={item.orderDetailId}>
              <td>{item.product.productName}</td>
              <td>{item.quantity}</td>
              <td>${item.price.toFixed(2)}</td>
              <td>${(item.quantity * item.price).toFixed(2)}</td>
            </tr>
          ))}
        </tbody>
      </Table>

      <Row className="mt-4">
        <Col>
          <Button variant="primary" onClick={downloadInvoicePDF} className="d-flex align-items-center">
            <FaDownload className="mr-2" /> Download Invoice
          </Button>
        </Col>
      </Row>
    </Container>
  );
};

export default InvoicePage;
