import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import "../styles/ProductList.css";
import AuthContext from "../AuthContext";

const ProductList = () => {
    const [products, setProducts] = useState([]);
    const [message, setMessage] = useState("");
    const { user } = useContext(AuthContext);
    const userId = user?.userId;
    const [isMember, setIsMember] = useState(false);
    const [superCoins, setSuperCoins] = useState(0);
    const [useSuperCoins, setUseSuperCoins] = useState({});

    useEffect(() => {
        fetchProducts();
        if (userId) {
            checkMembershipStatus();
            fetchSuperCoins();
        }
    }, [userId]);

    const fetchProducts = async () => {
        try {
            const response = await axios.get("http://localhost:8080/api/products");
            setProducts(Array.isArray(response.data) ? response.data : []);
        } catch (error) {
            console.error("Error fetching products:", error);
            setProducts([]);
        }
    };

    const checkMembershipStatus = async () => {
        try {
            const response = await axios.get(`http://localhost:8080/users/${userId}`);
            setIsMember(response.data?.member || false);
        } catch (error) {
            console.error("Error checking membership status:", error);
        }
    };

    const fetchSuperCoins = async () => {
        try {
            const response = await axios.get(`http://localhost:8080/api/supercoins/${userId}`);
            setSuperCoins(response.data?.balance || 0);
        } catch (error) {
            console.error("Error fetching SuperCoins:", error);
            setSuperCoins(0);
        }
    };

    const addToCart = async (productId, quantity = 1) => {
        if (!userId) {
            setMessage("‚ö† Please login to add items to the cart.");
            return;
        }
        try {
            const response = await axios.post(
                "http://localhost:8080/api/cart/add",
                null,
                { params: { userId, productId, quantity } }
            );
            if (response.status === 200) {
                setMessage("‚úÖ Product added to cart successfully!");
            }
        } catch (error) {
            console.error("Error adding to cart:", error);
            setMessage("‚ùå Failed to add product to cart.");
        }
    };

    const toggleSuperCoins = (productId) => {
        setUseSuperCoins((prevState) => ({
            ...prevState,
            [productId]: !prevState[productId]
        }));
    };

    return (
        <>
            {/* Heading */}
            <div className="product-container">
                <h1>Our Products</h1>
            </div>

            {/* Message Container */}
            {message && <div className="alert-message" style={{ color: "red" }}>{message}</div>}

            {/* Product Grid */}
            <div className="product-container">
                <div className="product-grid">
                    {products.length > 0 ? (
                        products.map((product) => {
                            const discountedPrice = (product.price * 0.9).toFixed(2);
                            const coinDiscount = Math.min(superCoins, 50);
                            const isChecked = useSuperCoins[product.productId] || false;
                            const finalPrice = (isMember ? discountedPrice : product.price) - (isChecked ? coinDiscount : 0);

                            return (
                                <div className="product-card" key={product.productId}>
                                    <img
                                        src={product.producturl || "https://via.placeholder.com/300"}
                                        alt={product.name}
                                        className="product-image"
                                    />
                                    <div className="product-info">
                                        <h3>{product.name}</h3>
                                        <p className="product-description">{product.description}</p>

                                        <div className="product-pricing">
                                            <p className="original-price"><del>‚Çπ{product.price}</del></p>
                                            <p className="discount-price">üè∑ ‚Çπ{discountedPrice}</p>
                                            <div className="supercoin-container">
                                                <input type="checkbox" checked={isChecked} onChange={() => toggleSuperCoins(product.productId)} />
                                                <label>üí∞ Use {coinDiscount} SuperCoins</label>
                                            </div>
                                            <p className="final-price">‚ö° ‚Çπ{finalPrice.toFixed(2)}</p>
                                        </div>

                                        <div className="product-buttons">
                                            <button className="add-to-cart" onClick={() => addToCart(product.productId, 2)}>Add to Cart</button>
                                            <button className="buy-now">Buy Now</button>
                                        </div>
                                    </div>
                                </div>
                            );
                        })
                    ) : <p>No products found</p>}
                </div>
            </div>
        </>
    );
};

export default ProductList;
