import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Header from "./Header";
import Footer from "./Footer";
import "../styles/Dashboard.css";

const Dashboard = () => {
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    setTimeout(() => {
      setIsLoading(false);
    }, 3000);
  }, []);

  return (
    <div>
      {isLoading && (
        <div className="preloader">
          <div className="spinner"></div>
        </div>
      )}

      <Header />

      {/* Hero Section - Carousel */}
      <div className="hero-section">
        <div id="carouselExampleControls" className="carousel slide" data-bs-ride="carousel">
          <div className="carousel-inner">
            <div className="carousel-item active">
              <img src="https://source.unsplash.com/1200x500/?shopping" className="d-block w-100" alt="Slide 1" />
            </div>
            <div className="carousel-item">
              <img src="https://source.unsplash.com/1200x500/?fashion" className="d-block w-100" alt="Slide 2" />
            </div>
            <div className="carousel-item">
              <img src="https://source.unsplash.com/1200x500/?electronics" className="d-block w-100" alt="Slide 3" />
            </div>
            <div className="carousel-item">
              <img src="https://source.unsplash.com/1200x500/?sale" className="d-block w-100" alt="Slide 4" />
            </div>
          </div>
          <button className="carousel-control-prev" type="button" data-bs-target="#carouselExampleControls" data-bs-slide="prev">
            <span className="carousel-control-prev-icon" aria-hidden="true"></span>
            <span className="visually-hidden">Previous</span>
          </button>
          <button className="carousel-control-next" type="button" data-bs-target="#carouselExampleControls" data-bs-slide="next">
            <span className="carousel-control-next-icon" aria-hidden="true"></span>
            <span className="visually-hidden">Next</span>
          </button>
        </div>
      </div>

      {/* Content Section */}
      <div className="container my-5">
        <h2 className="text-center mb-4">Welcome to E-Mart</h2>
        <p className="text-center">
          Discover the latest trends in fashion, electronics, and more! Shop now and enjoy exclusive deals and discounts.
        </p>
        <div className="text-center">
          <a href="/productpage" className="btn btn-primary btn-lg">Start Shopping</a>
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default Dashboard;
