import React, { useState } from "react";
import axios from "axios";

const Register = () => {
  const [user, setUser] = useState({
    name: "",
    mobileNumber: "",
    email: "",
    address: "",
    password: "",
  });

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:8080/users/register", user);
      alert("User registered successfully");
    } catch (error) {
      alert("Error registering user");
    }
  };

  return (
    <div className="container">
      <h2>Register</h2>
      <form onSubmit={handleRegister}>
        <input type="text" placeholder="Name" onChange={(e) => setUser({ ...user, name: e.target.value })} required />
        <input type="text" placeholder="Mobile" onChange={(e) => setUser({ ...user, mobileNumber: e.target.value })} required />
        <input type="email" placeholder="Email" onChange={(e) => setUser({ ...user, email: e.target.value })} required />
        <input type="text" placeholder="Address" onChange={(e) => setUser({ ...user, address: e.target.value })} />
        <input type="password" placeholder="Password" onChange={(e) => setUser({ ...user, password: e.target.value })} required />
        <button type="submit">Register</button>
      </form>
    </div>
  );
};

export default Register;
