import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "../styles/Footer.css"; // Import the updated CSS file

const Footer = () => {
  const [location, setLocation] = useState("Fetching location...");
  const [currentTime, setCurrentTime] = useState(new Date().toLocaleTimeString());

  // Fetch user location using API
  useEffect(() => {
    fetch("https://ipapi.co/json/")
      .then((response) => response.json())
      .then((data) => setLocation(`${data.city}, ${data.country_name}`))
      .catch(() => setLocation("Location not available"));
  }, []);

  // Update time every second
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date().toLocaleTimeString());
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  return (
    <footer className="footer">
      <div className="container">
        <div className="footer-row">
          <div className="footer-column">
            <h6>Company</h6>
            <p>&copy; 2025 Unstoppable GRP3</p>
            <p><a href="/about">About Us</a></p>
          </div>
          <div className="footer-column">
            <h6>Contact</h6>
            <p>Email: support@emart.com</p>
            <p>Toll-Free: 1800-123-4567</p>
          </div>
          <div className="footer-column">
            <h6>Address</h6>
            <p>123 E-Mart Street</p>
            <p>Commerce City, USA</p>
          </div>
          <div className="footer-column">
            <h6>Location & Time</h6>
            <p>üìç {location}</p>
            <p>üïí {currentTime}</p>
          </div>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
