import React, { useState, useEffect } from "react";
import "../styles/Advertisements.css";

const adData = [
  { 
    img: "https://www.lebow.drexel.edu/sites/default/files/story/2022-11/time_scarcity_node.jpg", 
    link: "/deals", 
    alt: "Limited Time Deals" 
  },
  { 
    img: "https://www.gizmochina.com/wp-content/uploads/2018/06/GearBest-Electronics.jpg", 
    link: "/electronics", 
    alt: "New Arrivals in Electronics" 
  },
  { 
    img: "https://assets.vogue.com/photos/67326f4b73bb4a1576de6ae0/master/w_2560%2Cc_limit/Collage.jpg", 
    link: "/fashion", 
    alt: "Trendy Fashion Collection" 
  },
  { 
    img: "https://assets.architecturaldigest.in/photos/60082f023a9d9570bc84160b/16:9/w_2560%2Cc_limit/Home-upgrade-perfect-for-monsoon-1366x768.jpg", 
    link: "/home-decor", 
    alt: "Upgrade Your Home Decor" 
  },
];

const Advertisements = () => {
  const [currentAd, setCurrentAd] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentAd((prevAd) => (prevAd + 1) % adData.length);
    }, 2500); // Change ad every 4 seconds
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="advertisements">
      <div className="ad-banner">
        {adData.map((ad, index) => (
          <a 
            key={index} 
            href={ad.link} 
            className={`ad-item ${index === currentAd ? "active" : ""}`}
          >
            <img src={ad.img} alt={ad.alt} />
          </a>
        ))}
      </div>
    </div>
  );
};

export default Advertisements;
