import { useState } from "react";
import { registerUser } from "../api";
import { useNavigate } from "react-router-dom";
import "../styles/register.css";

const Register = () => {
  const [userData, setUserData] = useState({
    name: "",
    email: "",
    mobileNumber: "",
    password: "",
    address: "",
  });

  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const validate = () => {
    let tempErrors = {};

    if (userData.name.length < 3 || userData.name.length > 30) {
      tempErrors.name = "Name must be between 3 and 30 characters.";
    }

    if (!/^\S+@\S+\.\S+$/.test(userData.email)) {
      tempErrors.email = "Enter a valid email address.";
    }

    if (!/^\d{10}$/.test(userData.mobileNumber)) {
      tempErrors.mobileNumber = "Mobile number must be exactly 10 digits.";
    }

    if (userData.password.length < 8) {
      tempErrors.password = "Password must be at least 8 characters long.";
    }

    if (!userData.address.trim()) {
      tempErrors.address = "Address cannot be empty.";
    }

    setErrors(tempErrors);
    return Object.keys(tempErrors).length === 0;
  };

  const handleChange = (e) => {
    setUserData({ ...userData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (validate()) {
      try {
        await registerUser(userData);
        alert("Registration Successful!");
        navigate("/login");
      } catch (error) {
        alert("Registration Failed!");
      }
    }
  };

  return (
    <div className="register-container">
      <div className="register-box">
        <img
          src="https://media.giphy.com/media/xT0xeJpnrWC4XWblEk/giphy.gif"
          alt="Welcome GIF"
          className="register-gif"
        />

        <h2>Register to eMart</h2>
        <form className="register-form" onSubmit={handleSubmit}>
          <input type="text" name="name" placeholder="Name" onChange={handleChange} required />
          {errors.name && <p className="error">{errors.name}</p>}

          <input type="email" name="email" placeholder="Email" onChange={handleChange} required />
          {errors.email && <p className="error">{errors.email}</p>}

          <input type="text" name="mobileNumber" placeholder="Mobile Number" onChange={handleChange} required />
          {errors.mobileNumber && <p className="error">{errors.mobileNumber}</p>}

          <input type="password" name="password" placeholder="Password" onChange={handleChange} required />
          {errors.password && <p className="error">{errors.password}</p>}

          <textarea name="address" placeholder="Address" onChange={handleChange} required></textarea>
          {errors.address && <p className="error">{errors.address}</p>}

          <button type="submit" className="register-btn">Register</button>
          <button type="button" className="register-btn secondary" onClick={() => navigate("/login")}>
            Login
          </button>
        </form>
      </div>
    </div>
  );
};

export default Register;
