import React, { useEffect, useState, useContext } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Carousel } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import Header from "./Header";
import Chatbot from "./Chatbot";
import AuthContext from "../AuthContext";
import "../styles/Dashboard.css";

const Dashboard = () => {
    const [isLoading, setIsLoading] = useState(true);
    const [products, setProducts] = useState([]);
    const [message, setMessage] = useState("");
    const { user } = useContext(AuthContext);
    const userId = user?.userId;
    const navigate = useNavigate();
    const [isMember, setIsMember] = useState(false);
    const [superCoins, setSuperCoins] = useState(0);
    const [useSuperCoins, setUseSuperCoins] = useState({});

    useEffect(() => {
        setTimeout(() => setIsLoading(false), 2000);
        fetchProducts();

        if (userId) {
            checkMembershipStatus();
            fetchSuperCoins();
        }
    }, [userId]);

    const fetchProducts = async () => {
        try {
            const response = await axios.get("http://localhost:8080/api/products");
            setProducts(response.data);
        } catch (error) {
            console.error("Error fetching products:", error);
        }
    };

    const checkMembershipStatus = async () => {
        try {
            const response = await axios.get(`http://localhost:8080/users/${userId}`);
            setIsMember(response.data?.member || false);
        } catch (error) {
            console.error("Error checking membership status:", error);
        }
    };

    const fetchSuperCoins = async () => {
        try {
            const response = await axios.get(`http://localhost:8080/api/supercoins/${userId}`);
            setSuperCoins(response.data?.balance || 0);
        } catch (error) {
            console.error("Error fetching SuperCoins:", error);
            setSuperCoins(0);
        }
    };

    const toggleSuperCoins = (productId) => {
        setUseSuperCoins((prevState) => ({
            ...prevState,
            [productId]: !prevState[productId]
        }));
    };

    const addToCart = async (productId, quantity = 1) => {
        if (!userId) {
            setMessage("‚ö† Please login to add items to the cart.");
            return;
        }

        try {
            const response = await axios.post("http://localhost:8080/api/cart/add", null, {
                params: { userId, productId, quantity }
            });

            if (response.status === 200) {
                setMessage("‚úÖ Product added to cart successfully!");
                let storedCart = JSON.parse(localStorage.getItem("cart")) || [];
                storedCart.push({ productId, quantity });
                localStorage.setItem("cart", JSON.stringify(storedCart));
            }
        } catch (error) {
            console.error("Error adding to cart:", error);
            setMessage("‚ùå Failed to add product to cart.");
        }
    };

    return (
        <div>
            {isLoading && (
                <div className="preloader">
                    <div className="spinner"></div>
                </div>
            )}
            <Header />

            <div className="hero-section">
                <Carousel controls indicators interval={3000} pause={false} fade>
                    {["Electronic.jpg", "Clothes.jpg"].map((img, index) => (
                        <Carousel.Item key={index}>
                            <img src={`./Images/${img}`} className="d-block w-100" alt={`Banner ${index + 1}`} />
                            <Carousel.Caption>
                                <h3>
                                    {index === 0
                                        ? "Exclusive Deals on Electronics"
                                        : "Flash Sale - Up to 70% Off"}
                                </h3>
                                <p>
                                    {index === 0
                                        ? "Grab the latest gadgets at unbeatable prices!"
                                        : "Upgrade your wardrobe with trending styles."}
                                </p>
                                <a href={`/subcategories/${index + 2}`} className="btn btn-warning">
                                    Shop Now
                                </a>
                            </Carousel.Caption>
                        </Carousel.Item>
                    ))}
                </Carousel>
            </div>

            {message && <p className="alert-message">{message}</p>}

            <div className="container my-5">
                <h2 className="text-center mb-4">Featured Products</h2>
                <div className="row">
                    {products.map((product) => {
                        const discountedPrice = (product.price * 0.9).toFixed(2);
                        const coinDiscount = Math.min(superCoins, 50);
                        const isChecked = useSuperCoins[product.productId] || false;
                        const priceWithCoins = (
                            isMember
                                ? discountedPrice - (isChecked ? coinDiscount : 0)
                                : product.price - (isChecked ? coinDiscount : 0)
                        ).toFixed(2);

                        return (
                            <div key={product.productId} className="col-md-4 mb-4">
                                <div className="card product-card">
                                    <img
                                        src={product.producturl || "https://via.placeholder.com/150"}
                                        className="card-img-top"
                                        alt={product.name}
                                    />
                                    <div className="card-body text-center">
                                        <h5 className="card-title">{product.name}</h5>
                                        <p className="product-description">{product.description}</p>

                                        {/* Pricing Section */}
                                        <div className="product-pricing">
                                            <p className="price-label">
                                                <strong>Original Price:</strong>
                                            </p>
                                            <p className="original-price">
                                                <span>MRP: </span>
                                                {isMember ? <del>‚Çπ{product.price}</del> : `‚Çπ${product.price}`}
                                            </p>

                                            <p className="price-label">
                                                <strong>Discounted Price:</strong>
                                            </p>
                                            <p className="discount-price">
                                                <span>üè∑ Discounted Price: </span>
                                                {isMember ? `‚Çπ${discountedPrice}` : <del>‚Çπ{discountedPrice}</del>}
                                            </p>

                                            {/* SuperCoins Option */}
                                            <div className="supercoin-container">
                                                <input
                                                    type="checkbox"
                                                    id={`useSuperCoins-${product.productId}`}
                                                    checked={isChecked}
                                                    onChange={() => toggleSuperCoins(product.productId)}
                                                />
                                                <label htmlFor={`useSuperCoins-${product.productId}`}>
                                                    üí∞ Use {coinDiscount} SuperCoins
                                                </label>
                                            </div>

                                            {/* Final Price */}
                                            <p className="final-price">
                                                <span>‚ö° Final Price: </span>
                                                <span className="highlight-price">‚Çπ{priceWithCoins}</span>
                                            </p>
                                        </div>

                                        {/* Buttons */}
                                        <div className="product-buttons">
                                            <button
                                                className="btn btn-success"
                                                onClick={() => addToCart(product.productId, 2)}
                                            >
                                                Add to Cart
                                            </button>
                                            <button
                                                className="btn btn-primary"
                                                onClick={() => navigate(`/product/${product.productId}`)}
                                            >
                                                Buy Now
                                            </button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        );
                    })}
                </div>
            </div>

            <Chatbot products={products} />
        </div>
    );
};

export default Dashboard;
