import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/productList.css";

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [subcategories, setSubcategories] = useState([]);
  const [subSubcategories, setSubSubcategories] = useState([]);
  const [selectedFilters, setSelectedFilters] = useState({
    categories: [],
    subcategories: [],
    subSubcategories: [],
  });

  useEffect(() => {
    fetchProducts();
  }, []);

  // Fetch products from API
  const fetchProducts = async () => {
    try {
      const response = await axios.get("/api/products"); // Adjust API endpoint as needed
      const data = response.data;
      setProducts(data);
      setFilteredProducts(data);

      // Extract unique categories, subcategories, and sub-subcategories
      const uniqueCategories = [...new Set(data.map((item) => item.category))];
      const uniqueSubcategories = [
        ...new Set(data.map((item) => item.subcategory)),
      ];
      const uniqueSubSubcategories = [
        ...new Set(data.map((item) => item.subSubcategory)),
      ];

      setCategories(uniqueCategories);
      setSubcategories(uniqueSubcategories);
      setSubSubcategories(uniqueSubSubcategories);
    } catch (error) {
      console.error("Error fetching products:", error);
    }
  };

  // Handle filter selection
  const handleFilterChange = (type, value) => {
    setSelectedFilters((prevFilters) => {
      const updatedFilters = { ...prevFilters };

      if (updatedFilters[type].includes(value)) {
        updatedFilters[type] = updatedFilters[type].filter((item) => item !== value);
      } else {
        updatedFilters[type].push(value);
      }

      return updatedFilters;
    });
  };

  // Apply filters dynamically
  useEffect(() => {
    let filtered = products;

    if (selectedFilters.categories.length > 0) {
      filtered = filtered.filter((product) =>
        selectedFilters.categories.includes(product.category)
      );
    }
    if (selectedFilters.subcategories.length > 0) {
      filtered = filtered.filter((product) =>
        selectedFilters.subcategories.includes(product.subcategory)
      );
    }
    if (selectedFilters.subSubcategories.length > 0) {
      filtered = filtered.filter((product) =>
        selectedFilters.subSubcategories.includes(product.subSubcategory)
      );
    }

    setFilteredProducts(filtered);
  }, [selectedFilters, products]);

  return (
    <div className="product-list-container">
      <h2>Product List</h2>

      {/* Filter Sidebar */}
      <div className="filter-container">
        <h3>Filters</h3>

        <div className="filter-group">
          <h4>Category</h4>
          {categories.map((category, index) => (
            <label key={index} className="filter-option">
              <input
                type="checkbox"
                value={category}
                onChange={() => handleFilterChange("categories", category)}
              />
              {category}
            </label>
          ))}
        </div>

        <div className="filter-group">
          <h4>Subcategory</h4>
          {subcategories.map((subcategory, index) => (
            <label key={index} className="filter-option">
              <input
                type="checkbox"
                value={subcategory}
                onChange={() => handleFilterChange("subcategories", subcategory)}
              />
              {subcategory}
            </label>
          ))}
        </div>

        <div className="filter-group">
          <h4>Sub-Subcategory</h4>
          {subSubcategories.map((subSubcategory, index) => (
            <label key={index} className="filter-option">
              <input
                type="checkbox"
                value={subSubcategory}
                onChange={() => handleFilterChange("subSubcategories", subSubcategory)}
              />
              {subSubcategory}
            </label>
          ))}
        </div>
      </div>

      {/* Product Grid */}
      <div className="product-grid">
        {filteredProducts.length > 0 ? (
          filteredProducts.map((product) => (
            <div key={product.id} className="product-card">
              <img src={product.image} alt={product.name} />
              <h4>{product.name}</h4>
              <p>{product.category} → {product.subcategory} → {product.subSubcategory}</p>
              <p>Price: ${product.price}</p>
            </div>
          ))
        ) : (
          <p>No products found.</p>
        )}
      </div>
    </div>
  );
};

export default ProductList;
