// Dashboard.js

// Toggle hamburger menu on mobile view
const hamburger = document.querySelector('.hamburger');
const navList = document.querySelector('.nav-list ul');

hamburger.addEventListener('click', () => {
    navList.classList.toggle('active');
});

// Smooth scrolling to sections
const navLinks = document.querySelectorAll('.nav-list ul li a');

navLinks.forEach(link => {
    link.addEventListener('click', (e) => {
        e.preventDefault();
        const targetId = link.getAttribute('href').slice(1);
        const targetElement = document.getElementById(targetId);
        targetElement.scrollIntoView({ behavior: 'smooth' });
    });
});

// Search functionality
const searchButton = document.querySelector('button');
const searchInput = document.querySelector('#site-search');

searchButton.addEventListener('click', () => {
    const query = searchInput.value.toLowerCase();
    const projects = document.querySelectorAll('.project-item');

    projects.forEach(project => {
        const projectTitle = project.querySelector('h1').textContent.toLowerCase();
        if (projectTitle.includes(query)) {
            project.style.display = 'block';  // Show matching projects
        } else {
            project.style.display = 'none';  // Hide non-matching projects
        }
    });
});

// Form Validation (Example for Contact form or any form)
const contactForm = document.querySelector('.contact-form');
const submitButton = contactForm ? contactForm.querySelector('button') : null;

if (submitButton) {
    submitButton.addEventListener('click', (e) => {
        const emailInput = contactForm.querySelector('input[type="email"]');
        const messageInput = contactForm.querySelector('textarea');
        let isValid = true;

        if (!emailInput.value.includes('@')) {
            alert('Please enter a valid email address');
            isValid = false;
        }

        if (messageInput.value.trim() === '') {
            alert('Message cannot be empty');
            isValid = false;
        }

        if (!isValid) {
            e.preventDefault();  // Prevent form submission if invalid
        }
    });
}

// Example of adding a modal window (e.g., for project details)
const projectItems = document.querySelectorAll('.project-item');
const modal = document.querySelector('.modal');
const closeModalButton = modal ? modal.querySelector('.close') : null;

projectItems.forEach(project => {
    project.addEventListener('click', () => {
        const projectTitle = project.querySelector('h1').textContent;
        const projectDetails = project.querySelector('p').textContent;

        modal.querySelector('.modal-title').textContent = projectTitle;
        modal.querySelector('.modal-body').textContent = projectDetails;

        modal.classList.add('show');  // Show modal
    });
});

if (closeModalButton) {
    closeModalButton.addEventListener('click', () => {
        modal.classList.remove('show');  // Hide modal
    });
}
