import React, { useContext } from "react";
import { Link, useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "../styles/Header.css";
import AuthContext from "../AuthContext";
import profileIcon from "../assets/profile-icon.png"; // Add a profile icon in your assets folder

const Header = () => {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate("/");
  };

  return (
    <nav className="navbar navbar-expand-lg fixed-top shadow-lg animated-header">
      <div className="container-fluid">
        {/* Logo */}
        <Link className="navbar-brand fw-bold fs-3 logo" to="/dashboard">
          <img
            src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSF8ExH1zQPwZ4z3pa4gdCl76l9eJ4T7Ai8iw&s"
            alt="E-Mart Logo"
            className="header-logo"
          />
        </Link>

        {/* Navbar Toggler */}
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
          aria-controls="navbarNav"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>

        {/* Navbar Links */}
        <div className="collapse navbar-collapse justify-content-between" id="navbarNav">
          <ul className="navbar-nav mx-auto">
            <li className="nav-item"><Link className="nav-link fs-5" to="/dashboard">Home</Link></li>
            <li className="nav-item"><Link className="nav-link fs-5" to="/productpage">Products</Link></li>
            <li className="nav-item"><Link className="nav-link fs-5" to="/categorylist">Categories</Link></li>
            <li className="nav-item"><Link className="nav-link fs-5" to="/orders">Orders</Link></li>
            <li className="nav-item"><Link className="nav-link fs-5" to="/contact">Contact</Link></li>
          </ul>

          {/* Auth Section */}
          <div className="auth-section d-flex align-items-center">
            {user ? (
              <div className="d-flex align-items-center">
                <span className="text-light fw-bold user-name">{user.name}</span>
                <img
                  src={profileIcon}
                  alt="Profile"
                  className="profile-icon"
                  onClick={() => navigate("/profile")}
                />
                <button className="btn btn-danger fw-bold logout-btn" onClick={handleLogout}>Logout</button>
              </div>
            ) : (
              <button className="btn btn-light fw-bold login-btn" onClick={() => navigate("/login")}>Login</button>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Header;
