import React, { useEffect, useState, useContext } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Carousel } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import Header from "./Header";
import Chatbot from "./Chatbot"; // ✅ Import Chatbot component
import AuthContext from "../AuthContext";
import "../styles/Dashboard.css";

const Dashboard = () => {
  const [isLoading, setIsLoading] = useState(true);
  const [products, setProducts] = useState([]);
  const [message, setMessage] = useState(""); // ✅ For success/error messages
  const { user } = useContext(AuthContext);
  const userId = user?.userId; // ✅ Get user ID from context
  const navigate = useNavigate();

  useEffect(() => {
    setTimeout(() => setIsLoading(false), 2000);

    // ✅ Fetch products from API
    axios.get("http://localhost:8080/api/products")
      .then(response => setProducts(response.data))
      .catch(error => console.error("Error fetching products:", error));
  }, []);

  // ✅ Add product to cart using API
  const addToCart = async (productId, quantity = 1) => {
    if (!userId) {
      setMessage("⚠️ Please login to add items to the cart.");
      return;
    }

    try {
      const response = await axios.post(
        `http://localhost:8080/api/cart/add`,
        null, // No body, using params
        { params: { userId, productId, quantity } }
      );

      if (response.status === 200) {
        setMessage("✅ Product added to cart successfully!");

        // Store cartId in localStorage
        let storedCart = JSON.parse(localStorage.getItem("cart")) || [];
        storedCart.push({ productId, quantity });
        localStorage.setItem("cart", JSON.stringify(storedCart));
      }
    } catch (error) {
      console.error("Error adding to cart:", error);
      setMessage("❌ Failed to add product to cart.");
    }
  };

  return (
    <div>
      {/* ✅ Preloader */}
      {isLoading && (
        <div className="preloader">
          <div className="spinner"></div>
        </div>
      )}

      <Header />

      {/* ✅ Hero Section with Carousel */}
      <div className="hero-section">
        <Carousel controls indicators interval={3000} pause={false} fade>
          <Carousel.Item>
            <img src="./Images/Electronic.jpg" className="d-block w-100" alt="Shopping Banner 1" />
            <Carousel.Caption>
              <h3>Exclusive Deals on Electronics</h3>
              <p>Grab the latest gadgets at unbeatable prices!</p>
              <a href="/subcategories/2" className="btn btn-warning">Shop Now</a>
            </Carousel.Caption>
          </Carousel.Item>
          <Carousel.Item>
            <img src="./Images/Clothes.jpg" className="d-block w-100" alt="Shopping Banner 2" />
            <Carousel.Caption>
              <h3>Flash Sale - Up to 70% Off</h3>
              <p>Upgrade your wardrobe with trending styles.</p>
              <a href="/subcategories/3" className="btn btn-warning">Shop Now</a>
            </Carousel.Caption>
          </Carousel.Item>
        </Carousel>
      </div>

      {/* ✅ Success/Error Messages */}
      {message && <p className="alert-message">{message}</p>}

      {/* ✅ Products Section */}
      <div className="container my-5">
        <h2 className="text-center mb-4">Featured Products</h2>
        <div className="row">
          {products.map(product => (
            <div key={product.productId} className="col-md-4 mb-4">
              <div className="card product-card">
                <img 
                  src={product.producturl || "https://via.placeholder.com/150"} 
                  className="card-img-top" 
                  alt={product.productName} 
                />
                <div className="card-body text-center">
                  <h5 className="card-title">{product.productName}</h5>
                  <p className="describtion">{product.description}</p>
                 <b></b> <p className="card-text">Price: ₹{product.price}</p>
                  <div className="d-flex justify-content-center">
                    {/* ✅ Add to Cart Button */}
                    <button className="btn btn-success me-2" onClick={() => addToCart(product.productId, 1)}>
                      Add to Cart
                    </button>
                    {/* ✅ Show More Button (Navigates to ProductDetail) */}
                    <button className="btn btn-primary" onClick={() => navigate(`/product/${product.productId}`)}>
                      Buy Now 
                    </button>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* ✅ Chatbot Floating Icon */}
      <Chatbot products={products} />
    </div>
  );
};

export default Dashboard;
