import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import "../styles/ProductList.css";
import AuthContext from "../AuthContext";

const ProductList = () => {
    const [products, setProducts] = useState([]);
    const [message, setMessage] = useState(""); // Message for alerts
    const { user } = useContext(AuthContext); // Get logged-in user
    const userId = user?.userId; // Get user ID from context

    useEffect(() => {
        fetchProducts();
    }, []);

    // ✅ Fetch all products from the backend
    const fetchProducts = async () => {
        try {
            const response = await axios.get("http://localhost:8080/api/products");
            setProducts(Array.isArray(response.data) ? response.data : []);
        } catch (error) {
            console.error("Error fetching products:", error);
            setProducts([]);
        }
    };

    // ✅ Add product to cart using API and get cartId
    const addToCart = async (productId, quantity = 1) => {
        if (!userId) {
            setMessage("⚠️ Please login to add items to the cart.");
            return;
        }

        try {
            const response = await axios.post(
                `http://localhost:8080/api/cart/add`,
                null, // No request body, using URL params
                {
                    params: { userId, productId, quantity },
                }
            );

            if (response.status === 200) {
                const { cartId } = response.data; // Expecting backend to return cartId
                setMessage("✅ Product added to cart successfully!");

                // Store cartId in localStorage so Cart page can use it
                let storedCart = JSON.parse(localStorage.getItem("cart")) || [];
                storedCart.push({ cartId, productId, quantity });
                localStorage.setItem("cart", JSON.stringify(storedCart));
            }
        } catch (error) {
            console.error("Error adding to cart:", error);
            setMessage("❌ Failed to add product to cart.");
        }
    };

    return (
        <div className="product-container">
            <h2>Product List</h2>

            {/* Display alert message */}
            {message && <p className="alert-message">{message}</p>}

            {/* Product Grid */}
            <div className="product-grid">
                {products.length > 0 ? (
                    products.map((product, index) => (
                        <div className="product-card" key={product.productId || index}>
                            <img
                                src={product.pro || "https://via.placeholder.com/150"}
                                alt={product.name || "Product Image"}
                                className="product-image"
                            />
                            <div className="product-info">
                                <h3>{product.name}</h3>
                                <p className="product-description">{product.description}</p>
                                <p className="product-price">₹{product.price}</p>
                                <div className="product-buttons">
                                    <button className="add-to-cart" onClick={() => addToCart(product.productId, 2)}>
                                        Add to Cart
                                    </button>
                                    <button className="buy-now">Buy Now</button>
                                </div>
                            </div>
                        </div>
                    ))
                ) : (
                    <p className="no-products">No products found</p>
                )}
            </div>
        </div>
    );
};

export default ProductList;
