import React, { useState, useEffect } from "react";
import "../../styles/ProductList.css";

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [user, setUser] = useState(null);
  const [useSupercoins, setUseSupercoins] = useState(false);

  useEffect(() => {
    fetch("/api/products")
      .then((response) => response.json())
      .then((data) => setProducts(data));

    fetch(`/api/user`)
      .then((response) => response.json())
      .then((data) => setUser(data));
  }, []);

  const handleBuyNow = (product) => {
    console.log("Buying Now: ", product);
    // Add your buy now functionality here
  };

  return (
    <div className="product-list">
      {products.map((product) => {
        const discountedPrice = user?.isMember ? product.price * 0.9 : product.price;
        const supercoinValue = user?.supercoins / 10;
        const finalPrice = useSupercoins ? Math.max(0, discountedPrice - supercoinValue) : discountedPrice;

        return (
          <div className="product-card" key={product.id}>
            <img src={product.image} alt={product.name} className="product-image" />
            <h3 className="product-name">{product.name}</h3>
            <p className="product-price">
              {user?.isMember && <span className="original-price">₹{product.price}</span>} ₹{finalPrice.toFixed(2)}
            </p>
            {user && (
              <label className="supercoin-checkbox">
                <input 
                  type="checkbox" 
                  checked={useSupercoins} 
                  onChange={() => setUseSupercoins(!useSupercoins)}
                />
                Use Supercoins (100 Supercoins = ₹10)
              </label>
            )}
            <button className="buy-now-button" onClick={() => handleBuyNow(product)}>
              Buy Now
            </button>
          </div>
        );
      })}
    </div>
  );
};

export default ProductList;
