import React, { useState, useEffect, useContext } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import AuthContext from "../AuthContext";
import axios from "axios";
import { Form, Button, Card } from "react-bootstrap";
import { toast } from "react-toastify";
import "../styles/Payment.css";

const PaymentPage = () => {
  const { user, token } = useContext(AuthContext);
  const userId = user?.userId;
  const location = useLocation();
  const navigate = useNavigate();

  const { finalPrice, cartItems } = location.state || {};

  const [userDetails, setUserDetails] = useState(null);
  const [paymentMethod, setPaymentMethod] = useState("");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!userId) return;

    const fetchData = async () => {
      try {
        const userResponse = await axios.get(`http://localhost:8080/users/${userId}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUserDetails(userResponse.data);
      } catch (error) {
        console.error("Error fetching user details:", error);
        toast.error("Failed to fetch user details.");
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [userId, token]);

  // Handle Payment Submission
  const handlePayment = async () => {
    if (!paymentMethod) {
      toast.error("Please select a payment method.");
      return;
    }

    const paymentData = {
      userId,
      paymentMethod,
      amount: finalPrice || 0,
      status: "Completed",
    };

    try {
      await axios.post("http://localhost:8080/api/payments", paymentData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      toast.success("Payment successful!");
      navigate("/invoice", { state: { paymentData } }); // Redirect to invoice page
    } catch (error) {
      console.error("Payment failed:", error);
      toast.error("Payment failed. Try again.");
    }
  };

  return (
    <div className="payment-container">
      <h2 className="payment-title">💳 Payment Page</h2>

      {loading ? (
        <p>Loading payment details...</p>
      ) : (
        <>
          {userDetails ? (
            <div className="payment-form">
              <h4>User Details</h4>
              <Form>
                <Form.Group>
                  <Form.Label>Name</Form.Label>
                  <Form.Control type="text" value={userDetails.name} readOnly />
                </Form.Group>
                <Form.Group>
                  <Form.Label>Address</Form.Label>
                  <Form.Control type="text" value={userDetails.address} readOnly />
                </Form.Group>
                <Form.Group>
                  <Form.Label>Email</Form.Label>
                  <Form.Control type="email" value={userDetails.email} readOnly />
                </Form.Group>
                <Form.Group>
                  <Form.Label>Mobile</Form.Label>
                  <Form.Control type="text" value={userDetails.mobileNumber} readOnly />
                </Form.Group>
                <Form.Group>
                  <Form.Label>Payment Method</Form.Label>
                  <Form.Control
                    as="select"
                    value={paymentMethod}
                    onChange={(e) => setPaymentMethod(e.target.value)}
                  >
                    <option value="">Select Payment Method</option>
                    <option value="Cash on Delivery">Cash on Delivery</option>
                    <option value="Credit Card">Credit Card</option>
                    <option value="UPI">UPI</option>
                  </Form.Control>
                </Form.Group>
              </Form>
              <Button onClick={handlePayment} className="pay-button">
                Proceed to Pay
              </Button>
            </div>
          ) : (
            <p>No user details found.</p>
          )}

         

          <h4 className="product-title">🛍 Products in Order</h4>
          <h1>Final Price: ₹{finalPrice || 0}.t</h1> 
            <br></br>
          <div className="product-list">
         
            
            {cartItems && cartItems.length > 0 ? (
              cartItems.map(({ product, quantity }) => (
                <Card key={product.productId} className="product-card">
                  <Card.Img variant="top" src={product.producturl} alt={product.productName} />
                  <Card.Body>
                    <Card.Title>{product.productName}</Card.Title>
                    <Card.Text>{product.description}</Card.Text>
                    <p>
                      <strong>Quantity:</strong> {quantity}
                    </p>
                    <p>
                    </p>
                  </Card.Body>
                </Card>
              ))
            ) : (
              <p>No products found.</p>
            )}
          </div>
        </>
      )}
    </div>
  );
};

export default PaymentPage;
