import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "../styles/SubSubCategories.css";
import "bootstrap/dist/css/bootstrap.min.css";

const SubSubcategories = () => {
  const { subcategoryId } = useParams();
  const [subSubcategories, setSubSubcategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!subcategoryId) {
      setError("Invalid Subcategory ID");
      setLoading(false);
      return;
    }

    const fetchSubSubcategories = async () => {
      try {
        console.log(`Fetching data from: http://localhost:8080/api/subsubcategories/${subcategoryId}`);
        const response = await axios.get(
          `http://localhost:8080/api/subsubcategories/${subcategoryId}`
        );

        console.log("API Response Data:", response.data);

        // Store response as an array (adjust according to API response structure)
        setSubSubcategories(response.data.subsubcategories || []);
      } catch (err) {
        console.error("API Fetch Error:", err);
        setError("Error fetching sub-subcategories");
      } finally {
        setLoading(false);
      }
    };

    fetchSubSubcategories();
  }, [subcategoryId]);

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">{error}</div>;
  if (!subSubcategories.length) return <div className="text-center">No sub-subcategories found.</div>;

  return (
    <div className="container">
      <h2 className="text-center my-4">Sub-Subcategories</h2>
      <div className="subsubcategories-grid">
        {subSubcategories.map((subSubcategory) => (
          <div key={subSubcategory.id} className="subsubcategories-card">
            <img 
              src={subSubcategory.imageUrl || "https://via.placeholder.com/150"} 
              alt={subSubcategory.name} 
            />
            <h3>{subSubcategory.name}</h3>
            <p>{subSubcategory.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default SubSubcategories;
