import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "../styles/SubSubCategories.css";  
import "bootstrap/dist/css/bootstrap.min.css";

const SubSubcategories = () => {
  const { subcategoryId } = useParams(); // Get subcategoryId from URL
  const [subSubcategories, setSubSubcategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!subcategoryId) {
      setError("Invalid Subcategory ID");
      setLoading(false);
      return;
    }

    const fetchSubSubcategories = async () => {
      try {
        console.log(`Fetching data from: http://localhost:8080/api/subsubcategories/${subcategoryId}`);
        const response = await axios.get(
          `http://localhost:8080/api/subsubcategories/${subcategoryId}`
        );

        console.log("API Response Data:", response.data); // ✅ Log the response data

        if (response.data && response.data.products) {
          setSubSubcategories(response.data.products); // Assuming `products` is inside response data
        } else {
          setSubSubcategories([]); // Handle case when products are missing
        }
      } catch (err) {
        console.error("API Fetch Error:", err);
        setError("Error fetching sub-subcategories");
      } finally {
        setLoading(false);
      }
    };

    fetchSubSubcategories();
  }, [subcategoryId]);

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="container">
      <h2 className="text-center my-4">Sub-Subcategories</h2>
      <div className="row">
        {subSubcategories.length > 0 ? (
          subSubcategories.map((subSubcategory, index) => (
            <div key={subSubcategory.id || index} className="col-md-4 col-sm-6 mb-4">
              <div className="product-card">
                <img 
                  src={subSubcategory.imageUrl ? subSubcategory.imageUrl : "https://via.placeholder.com/150"} 
                  alt={subSubcategory.name} 
                  className="product-image" 
                />
                <div className="product-info">
                  <h4>{subSubcategory.name}</h4>
                  <p>{subSubcategory.description}</p>
                  <p className="price">₹{subSubcategory.price}</p>
                  <button className="btn btn-primary">Buy Now</button>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p className="text-center">No sub-subcategories found.</p>
        )}
      </div>
    </div>
  );
};

export default SubSubcategories;
