import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "../styles/ProductPage.css"; // Import CSS file
import "bootstrap/dist/css/bootstrap.min.css";

const ProductPage = () => {
  const { subcategoryId } = useParams(); // Get subcategoryId from URL
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Default image if no image URL is found
  const defaultImage = "https://via.placeholder.com/200";

  useEffect(() => {
    const fetchProductsBySubcategory = async () => {
      try {
        console.log("Fetching products...");

        // ðŸ”¹ Fix CORS Issue: Use a proxy or CORS-friendly request
        const response = await axios.get("http://localhost:8080/api/products", {
          headers: {
            "Access-Control-Allow-Origin": "*", // Temporary Fix (Backend should handle this)
          },
        });

        console.log("API Response:", response.data);
        setProducts(response.data);
        setLoading(false);
      } catch (error) {
        console.error("API Error:", error);
        setError("Failed to load products. Please try again.");
        setLoading(false);
      }
    };

    fetchProductsBySubcategory();
  }, [subcategoryId]); // Fetch whenever subcategoryId changes

  if (loading) return <div className="loading">Loading products...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="product-page container">
      <h2 className="text-center my-4">Products in Subcategory</h2>

      <div className="row">
        {products.map((product) => (
          <div key={product.id} className="col-md-4">
            <div className="card product-card">
              {/* Render product image if available, otherwise use default image */}
              <img
                src={product.imageUrl ? product.imageUrl : defaultImage}
                alt={product.productName}
                className="card-img-top product-image"
              />

              <div className="card-body">
                <h5 className="card-title">{product.productName}</h5>
                <p className="card-text description">{product.description}</p>
                <p className="card-text price text-success">â‚¹{product.price}</p>
                <div className="d-flex justify-content-between">
                  <button className="btn btn-primary">Add to Cart</button>
                  <button className="btn btn-warning">Shop Now</button>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductPage;
