import { useState } from "react";
import { registerUser } from "../api";
import { useNavigate } from "react-router-dom";
import "../styles/register.css";

const Register = () => {
  const [userData, setUserData] = useState({
    name: "",
    email: "",
    mobileNumber: "",
    password: "",
    address: "",
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    setUserData({ ...userData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await registerUser(userData);
      alert("Registration Successful!");
      navigate("/login");
    } catch (error) {
      alert("Registration Failed!");
    }
  };

  return (
    <div className="register-container">
      <div className="register-bg">
        {/* Animated background */}
      </div>

      <div className="register-box">
        {/* Optional Logo or GIF */}
        <img
          src="https://media.giphy.com/media/xT0xeJpnrWC4XWblEk/giphy.gif"
          alt="Welcome GIF"
          className="register-gif"
        />

        <h2>Register to eMart</h2>
        <form className="register-form" onSubmit={handleSubmit}>
          <input type="text" name="name" placeholder="Name" onChange={handleChange} required />
          <input type="email" name="email" placeholder="Email" onChange={handleChange} required />
          <input type="text" name="mobileNumber" placeholder="Mobile Number" onChange={handleChange} required />
          <input type="password" name="password" placeholder="Password" onChange={handleChange} required />
          <input type="text" name="address" placeholder="Address" onChange={handleChange} />

          <button type="submit" className="register-btn">Register</button>
          <button type="button" className="register-btn secondary" onClick={() => navigate("/login")}>
            Login
          </button>
        </form>
      </div>
    </div>
  );
};

export default Register;
