import React, { useEffect, useState, useContext } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Carousel } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import Header from "./Header";
import Chatbot from "./Chatbot";
import AuthContext from "../AuthContext";
import "../styles/Dashboard.css";

const Dashboard = () => {
  const [isLoading, setIsLoading] = useState(true);
  const [products, setProducts] = useState([]);
  const [message, setMessage] = useState("");
  const { user } = useContext(AuthContext);
  const userId = user?.userId;
  const navigate = useNavigate();
  const [selectedVariants, setSelectedVariants] = useState({});
  const [quantities, setQuantities] = useState({});
  const [isMember, setIsMember] = useState(false);
  const [useSuperCoins, setUseSuperCoins] = useState({});

  useEffect(() => {
    setTimeout(() => setIsLoading(false), 2000);
    axios.get("http://localhost:8080/api/products")
      .then(response => {
        setProducts(response.data);
        const initialVariants = {}, initialQuantities = {};
        response.data.forEach(product => {
          initialVariants[product.productId] = product.variants?.[0] || null;
          initialQuantities[product.productId] = 1;
        });
        setSelectedVariants(initialVariants);
        setQuantities(initialQuantities);
      })
      .catch(error => console.error("Error fetching products:", error));

    if (userId) {
      axios.get(`http://localhost:8080/users/${userId}`)
        .then(response => setIsMember(response.data?.member || false))
        .catch(error => console.error("Error checking membership status:", error));
    }
  }, [userId]);

  const handleVariantChange = (productId, newVariant) => {
    setSelectedVariants(prev => ({ ...prev, [productId]: newVariant }));
  };

  const handleQuantityChange = (productId, newQuantity) => {
    setQuantities(prev => ({ ...prev, [productId]: Math.max(1, newQuantity) }));
  };

  const toggleSuperCoins = (productId) => {
    setUseSuperCoins(prevState => ({ ...prevState, [productId]: !prevState[productId] }));
  };

  const addToCart = async (productId) => {
    if (!userId) {
      setMessage("‚ö† Please login to add items to the cart.");
      return;
    }

    const selectedVariant = selectedVariants[productId];
    const quantity = quantities[productId];

    try {
      const response = await axios.post("http://localhost:8080/api/cart/add", null, {
        params: { userId, productId, quantity, variant: selectedVariant }
      });

      if (response.status === 200) {
        setMessage("‚úÖ Product added to cart successfully!");
        let storedCart = JSON.parse(localStorage.getItem("cart")) || [];
        storedCart.push({ productId, quantity, variant: selectedVariant });
        localStorage.setItem("cart", JSON.stringify(storedCart));
      }
    } catch (error) {
      console.error("Error adding to cart:", error);
      setMessage("‚ùå Failed to add product to cart.");
    }
  };

  return (
    <div>
      {isLoading && <div className="preloader"><div className="spinner"></div></div>}
      <Header />

      <div className="hero-section">
        <Carousel controls indicators interval={3000} pause={false} fade>
          {["Electronic.jpg", "Clothes.jpg"].map((img, index) => (
            <Carousel.Item key={index}>
              <img src={`./Images/${img}`} className="d-block w-100" alt={`Banner ${index + 1}`} />
              <Carousel.Caption>
                <h3>{index === 0 ? "Exclusive Deals on Electronics" : "Flash Sale - Up to 70% Off"}</h3>
                <p>{index === 0 ? "Grab the latest gadgets at unbeatable prices!" : "Upgrade your wardrobe with trending styles."}</p>
                <a href={`/subcategories/${index + 2}`} className="btn btn-warning">Shop Now</a>
              </Carousel.Caption>
            </Carousel.Item>
          ))}
        </Carousel>
      </div>

      {message && <p className="alert-message">{message}</p>}

      <div className="container my-5">
        <h2 className="text-center mb-4">Featured Products</h2>
        <div className="row">
          {products.map(product => {
            const discountedPrice = (product.price * 0.9).toFixed(2);  // 10% discount for members
            const isChecked = useSuperCoins[product.productId] || false;
            const priceWithCoins = (isMember
              ? (discountedPrice - (isChecked ? 50 : 0)).toFixed(2)
              : (product.price - (isChecked ? 50 : 0)).toFixed(2));

            return (
              <div key={product.productId} className="col-md-4 mb-4">
                <div className="card product-card">
                  <img src={product.producturl || "https://via.placeholder.com/150"} className="card-img-top" alt={product.name} />
                  <div className="card-body text-center">
                    <h5 className="card-title">{product.name}</h5>

                    {product.variants?.length > 0 && (
                      <select className="form-select" value={selectedVariants[product.productId]} onChange={(e) => handleVariantChange(product.productId, e.target.value)}>
                        {product.variants.map(variant => <option key={variant} value={variant}>{variant}</option>)}
                      </select>
                    )}

                    <p><del>‚Çπ{product.price}</del> <span className="text-success">‚Çπ{discountedPrice}</span> (Prime Member)</p>

                    {/* SuperCoins Option */}
                    <div className="supercoin-container">
                      <input
                        type="checkbox"
                        checked={isChecked}
                        onChange={() => toggleSuperCoins(product.productId)}
                      />
                      <label>
                        üí∞ Use 50 SuperCoins
                      </label>
                    </div>

                    {/* Final Price */}
                    <p>‚ö° <span className="text-danger">‚Çπ{priceWithCoins}</span> (After SuperCoins)</p>

                    <button className="btn btn-success" onClick={() => addToCart(product.productId)}>Add to Cart</button>
                    <button className="btn btn-primary" onClick={() => navigate(`/product/${product.productId}`)}>Buy Now</button>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>

      <Chatbot products={products} />
    </div>
  );
};

export default Dashboard;
