import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Header from "./Header";
import "../styles/Dashboard.css";

const Dashboard = () => {
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    setTimeout(() => {
      setIsLoading(false);
    }, 2000);
  }, []);

  return (
    <div>
      {isLoading && (
        <div className="preloader">
          <div className="spinner"></div>
        </div>
      )}

      <Header />

      {/* Hero Section - Carousel */}
      <div className="hero-section">
        <div id="carouselExampleControls" className="carousel slide" data-bs-ride="carousel">
          <div className="carousel-inner">
            <div className="carousel-item active">
              <img src="https://source.unsplash.com/1600x600/?shopping" className="d-block w-100" alt="Slide 1" />
            </div>
            <div className="carousel-item">
              <img src="https://source.unsplash.com/1600x600/?fashion" className="d-block w-100" alt="Slide 2" />
            </div>
            <div className="carousel-item">
              <img src="https://source.unsplash.com/1600x600/?electronics" className="d-block w-100" alt="Slide 3" />
            </div>
          </div>
        </div>
      </div>

      {/* Content Section */}
      <div className="container my-5 text-center">
        <h2>Welcome to E-Mart</h2>
        <p>Discover the latest trends in fashion, electronics, and more!</p>
        <a href="/productpage" className="btn btn-primary btn-lg">Start Shopping</a>
      </div>
    </div>
  );
};

export default Dashboard;
