import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import AuthContext from "../AuthContext";
import axios from "axios";
import { Form, Button, Card } from "react-bootstrap";
import { toast } from "react-toastify";
import "../styles/Payment.css";

const PaymentPage = () => {
  const { user, token } = useContext(AuthContext);
  const userId = user?.userId;
  const navigate = useNavigate();

  const [userDetails, setUserDetails] = useState({});
  const [paymentMethod, setPaymentMethod] = useState("");
  const [orderDetails, setOrderDetails] = useState([]);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (userId) {
      fetchUserDetails();
      fetchOrders();
    }
  }, [userId]);

  const fetchUserDetails = async () => {
    try {
      const response = await axios.get(`http://localhost:8080/users/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUserDetails(response.data);
      console.log("User Details:", response.data); // ‚úÖ Log user details
    } catch (error) {
      toast.error("Failed to fetch user details.");
      console.error("Error fetching user details:", error);
    }
  };

  const fetchOrders = async () => {
    try {
      const response = await axios.get(
        `http://localhost:8080/api/orders/user/${userId}`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      console.log("Orders Data:", response.data); // ‚úÖ Log orders data

      const orders = response.data;
      if (orders.length > 0) {
        fetchOrderDetails(orders[0].orderId);
      }
    } catch (error) {
      toast.error("Failed to fetch orders.");
      console.error("Error fetching orders:", error);
    }
  };

  const fetchOrderDetails = async (orderId) => {
    try {
      const response = await axios.get(
        `http://localhost:8080/api/orderdetails/order/${orderId}`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setOrderDetails(response.data);
      console.log("Order Details:", response.data); // ‚úÖ Log order details

      fetchProductDetails(response.data);
    } catch (error) {
      toast.error("Failed to fetch order details.");
      console.error("Error fetching order details:", error);
    }
  };

  const fetchProductDetails = async (orderDetails) => {
    try {
      const productRequests = orderDetails.map((item) =>
        axios.get(`http://localhost:8080/api/products/${item.productId}`, {
          headers: { Authorization: `Bearer ${token}` },
        })
      );

      const responses = await Promise.all(productRequests);
      const productsData = responses.map((res) => res.data);
      setProducts(productsData);
      setLoading(false);
      console.log("Product Details:", productsData); // ‚úÖ Log product details
    } catch (error) {
      toast.error("Failed to fetch product details.");
      console.error("Error fetching product details:", error);
      setLoading(false);
    }
  };

  const handlePayment = async () => {
    if (!paymentMethod) {
      toast.error("Please select a payment method.");
      return;
    }

    const paymentData = {
      userId,
      paymentMethod,
      amount: orderDetails.reduce((sum, item) => sum + item.totalPrice, 0),
      status: "Completed",
    };

    console.log("Payment Data Sent:", paymentData); // ‚úÖ Log payment request data

    try {
      await axios.post("http://localhost:8080/api/paymentdetails", paymentData, {
        headers: { Authorization: `Bearer ${token}` },
      });

      toast.success("Payment successful!");
      navigate("/order-confirmed");
    } catch (error) {
      toast.error("Payment failed.");
      console.error("Error processing payment:", error);
    }
  };

  return (
    <div className="payment-container">
      <h2 className="payment-title">üí≥ Payment Page</h2>

      {loading ? (
        <p>Loading payment details...</p>
      ) : (
        <>
          <div className="payment-form">
            <h4>User Details</h4>
            <Form>
              <Form.Group>
                <Form.Label>Name</Form.Label>
                <Form.Control type="text" value={userDetails.name} readOnly />
              </Form.Group>
              <Form.Group>
                <Form.Label>Address</Form.Label>
                <Form.Control type="text" value={userDetails.address} readOnly />
              </Form.Group>
              <Form.Group>
                <Form.Label>Email</Form.Label>
                <Form.Control type="email" value={userDetails.email} readOnly />
              </Form.Group>
              <Form.Group>
                <Form.Label>Mobile</Form.Label>
                <Form.Control type="text" value={userDetails.phone} readOnly />
              </Form.Group>
              <Form.Group>
                <Form.Label>Payment Method</Form.Label>
                <Form.Control
                  as="select"
                  value={paymentMethod}
                  onChange={(e) => setPaymentMethod(e.target.value)}
                >
                  <option value="">Select Payment Method</option>
                  <option value="Cash on Delivery">Cash on Delivery</option>
                  <option value="Credit Card">Credit Card</option>
                  <option value="UPI">UPI</option>
                </Form.Control>
              </Form.Group>
            </Form>
            <Button onClick={handlePayment} className="pay-button">
              Proceed to Pay
            </Button>
          </div>

          <h4 className="product-title">üõç Products in Order</h4>
          <div className="product-list">
            {products.map((product, index) => (
              <Card key={product.id} className="product-card">
                <Card.Img variant="top" src={product.image} className="product-image" />
                <Card.Body>
                  <Card.Title>{product.name}</Card.Title>
                  <Card.Text>{product.description}</Card.Text>
                  <p>
                    <strong>Quantity:</strong> {orderDetails[index]?.quantity}
                  </p>
                  <p>
                    <strong>Price:</strong> ‚Çπ{orderDetails[index]?.totalPrice}
                  </p>
                </Card.Body>
              </Card>
            ))}
          </div>
        </>
      )}
    </div>
  );
};

export default PaymentPage;
