import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import AuthContext from "../AuthContext"; // Import AuthContext to get the logged-in user
import "../styles/Cart.css";

const Cart = () => {
  const { user } = useContext(AuthContext); // Get logged-in user details
  const userId = user?.userId; // Ensure userId is dynamically assigned

  const [cartItems, setCartItems] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!userId) return; // Prevent API call if user is not logged in
    fetchCartItems();
  }, [userId]);

  // Fetch Cart Items from Backend
  const fetchCartItems = async () => {
    try {
      const response = await axios.get(`http://localhost:8080/api/cart/user/${userId}`);
      setCartItems(response.data);
    } catch (error) {
      setError("Failed to fetch cart items");
      console.error("Error fetching cart items:", error);
    }
  };

  // Update Cart Item Quantity
  const updateQuantity = async (cartId, newQuantity) => {
    if (newQuantity < 1) return;
    try {
      await axios.put(`http://localhost:8080/api/cart/update/${cartId}`, null, {
        params: { quantity: newQuantity },
      });
      fetchCartItems(); // Refresh cart after update
    } catch (error) {
      console.error("Error updating cart item:", error);
    }
  };

  // Remove Item from Cart
  const removeFromCart = async (cartId) => {
    try {
      await axios.delete(`http://localhost:8080/api/cart/remove/${cartId}`);
      fetchCartItems(); // Refresh cart after removal
    } catch (error) {
      console.error("Error removing item from cart:", error);
    }
  };

  // Calculate Total Price
  const getTotalPrice = () => {
    return cartItems.reduce((total, item) => total + item.product.price * item.quantity, 0);
  };

  return (
    <div className="cart-container">
      <h2 className="cart-title">Shopping Cart</h2>
      {error ? (
        <p className="error-text">{error}</p>
      ) : cartItems.length === 0 ? (
        <p className="empty-cart">Your cart is empty.</p>
      ) : (
        <>
          <div className="cart-items">
            {cartItems.map((item) => (
              <div key={item.id} className="cart-item">
                <img src={item.product.image} alt={item.product.name} className="cart-item-image" />
                <div className="cart-item-details">
                  <h3>{item.product.name}</h3>
                  <p>Price: ₹{item.product.price}</p>
                  <div className="cart-item-actions">
                    <button onClick={() => updateQuantity(item.id, item.quantity - 1)}>-</button>
                    <span>{item.quantity}</span>
                    <button onClick={() => updateQuantity(item.id, item.quantity + 1)}>+</button>
                  </div>
                  <p className="item-total">Total: ₹{item.product.price * item.quantity}</p>
                  <button onClick={() => removeFromCart(item.id)} className="remove-btn">Remove</button>
                </div>
              </div>
            ))}
          </div>

          {/* Billing Section */}
          <div className="cart-summary">
            <h3>Order Summary</h3>
            <p>Subtotal ({cartItems.length} items): <strong>₹{getTotalPrice()}</strong></p>
            <button className="checkout-btn">Proceed to Buy</button>
          </div>
        </>
      )}
    </div>
  );
};

export default Cart;
