import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "../styles/ProductPage.css"; // Import CSS file


const ProductPage = () => {
  const { subcategoryId } = useParams(); // Access subcategoryId from the URL
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Default image if no image is available in the database
  const defaultImage = "https://via.placeholder.com/200";

  useEffect(() => {
    const fetchProductsBySubcategory = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/products/subcategory/${subcategoryId}`);
        setProducts(response.data);
        setLoading(false);
      } catch (error) {
        setError("Failed to load products. Please try again.");
        setLoading(false);
      }
    };

    fetchProductsBySubcategory();
  }, [subcategoryId]);

  if (loading) return <div className="loading">Loading products...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="product-page">
      <h2>Products in Subcategory</h2>
      <div className="product-grid">
        {products.map((product) => (
          <div key={product.id} className="product-card">
            {/* Render product image if available, otherwise use default image */}
            <img src={product.imageUrl ? product.imageUrl : defaultImage} alt={product.productName} className="product-image" />
            <h3>{product.productName}</h3>
            <p className="description">{product.description}</p>
            <p className="price">â‚¹{product.price}</p>
            <div className="product-buttons">
              <button className="add-to-cart">Add to Cart</button>
              <button className="shop-now">Shop Now</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductPage;
