import React, { useEffect, useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import "../styles/Orders.css";
import AuthContext from "../AuthContext"; // Import AuthContext

const Orders = () => {
  const [orders, setOrders] = useState([]);
  const { user, token } = useContext(AuthContext); // Get logged-in user details
  const navigate = useNavigate();

  useEffect(() => {
    if (!user || !user.userId) {
      console.warn("No user is logged in.");
      return;
    }

    const fetchOrders = async () => {
      try {
        // Fetch all orders
        const response = await axios.get(`http://localhost:8080/api/orders`, {
          headers: { Authorization: `Bearer ${token}` }, // Pass token for authentication
        });

        const allOrders = response.data || [];

        // Filter orders where `order.userId` matches the logged-in `user.userId`
        const userOrders = allOrders.filter(order => order.userId === user.userId);

        setOrders(userOrders);
      } catch (error) {
        console.error("Error fetching orders:", error);
        setOrders([]); // Reset orders list on error
      }
    };

    fetchOrders();
  }, [user, token]); // Refetch when user or token changes

  return (
    <div className="container mt-5">
      <h2 className="mb-4 text-center">Your Orders</h2>

      {orders.length === 0 ? (
        <p className="text-center">No orders found.</p>
      ) : (
        <ul className="order-list">
          {orders.map((order) => (
            <li key={order.orderId} className="order-item">
              <span>Order ID: #{order.orderId}</span>
              <span>Date: {new Date(order.orderDate).toLocaleString()}</span>
              <span>Total: â‚¹{order.totalAmount.toFixed(2)}</span>
              <button
                className="btn btn-primary btn-sm"
                onClick={() => navigate(`/order-details/${order.order_id}`)}
              >
                View Details
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Orders;
