import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles/SubSubcategories.css"; 
import "bootstrap/dist/css/bootstrap.min.css";

const SubSubcategories = () => {
  const { subcategoryId } = useParams();
  const [subSubcategories, setSubSubcategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    if (!subcategoryId) {
      setError("Invalid Subcategory ID");
      setLoading(false);
      return;
    }

    const fetchSubSubcategories = async () => {
      try {
        console.log(`Fetching from: http://localhost:8080/api/subsubcategories/${subcategoryId}`);
        const response = await axios.get(`http://localhost:8080/api/subsubcategories/${subcategoryId}`);

        console.log("API Response Data:", response.data);
        setSubSubcategories(response.data);
      } catch (err) {
        console.error("API Fetch Error:", err);
        setError("Error fetching sub-subcategories");
      } finally {
        setLoading(false);
      }
    };

    fetchSubSubcategories();
  }, [subcategoryId]);

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="container">
      <h2 className="category-title">Sub-Subcategories</h2>
      <div className="row">
        {subSubcategories.length > 0 ? (
          subSubcategories.map((subsub) => (
            <div key={subsub.id} className="col-md-4">
              <div className="subsubcategories-card" onClick={() => navigate(`/products-by-category/${subsub.name}`)}>
                <img 
                  src={subsub.imageUrl || "https://via.placeholder.com/250"} 
                  alt={subsub.name} 
                  className="subsubcategories-image"
                />
                <div className="subsubcategories-details">
                  <h5>{subsub.name}</h5>
                  <p>Belongs to: {subsub.subcategory?.subcategoryName}</p>
                  <p>Category: {subsub.subcategory?.category?.categoryName}</p>
                </div>
              </div>
            </div>
          ))
        ) : (
          <div className="text-center">No sub-subcategories found.</div>
        )}
      </div>
    </div>
  );
};

export default SubSubcategories;
