import { BrowserRouter as Router, Routes, Route,use } from "react-router-dom";
//BrowseRouther keep history of navigation
//routes is a container for all the routes
//it contain the current navigation path

import { AuthProvider } from "./AuthContext";
//it will give the authentication related data and methods while logged-in 


//importing page data
import Register from "./pages/Register";
import Login from "./pages/Login";
import Dashboard from "./pages/Dashboard";
import ProtectedRoute from "./components/ProtectedRoute";
const RedirectToLogin = () => {
  const navigate = useNavigate();

  useEffect(() => {
    navigate("/login"); // Redirect to login when the app starts
  }, []);

  return null; // No UI needed
};

function App() {
  //component that wraps entire logic
  //inside authProvider component have access to authentication context
  return (
    <AuthProvider>      
      <Router>
        <Routes>
          <Route path="/register" element={<Register />} />
          <Route path="/login" element={<Login />} />
          <Route
            path="/dashboard"
            element={<ProtectedRoute Component={Dashboard} />}
          />
        </Routes>
      </Router>
    </AuthProvider>
  );
}

//element={<ProtectedRoute Component={Dashboard} />} it will check the user is authenticated or not

export default App;
