import React, { useEffect, useState, useContext } from "react";
import AuthContext from "../AuthContext"; // Import AuthContext
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import "../styles/Profile.css"; // Custom styles

const Profile = () => {
  const authContext = useContext(AuthContext); // ✅ Always call Hook
  const [userData, setUserData] = useState(null); // ✅ Always initialized
  const [error, setError] = useState(null);       // ✅ Always initialized

  const userId = authContext?.user?.userId;
  const token = authContext?.token;

  useEffect(() => {
    if (!userId) {
      console.warn("User ID is not available");
      return;
    }

    const fetchUserProfile = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/users/${userId}`, {
          headers: { Authorization: `Bearer ${token}` }, // Attach token
        });
        setUserData(response.data);
        setError(null);
      } catch (err) {
        setError("Failed to fetch user profile");
        console.error(err);
      }
    };

    fetchUserProfile();
  }, [userId, token]); // ✅ Dependencies handled properly

  if (!authContext) {
    console.error("AuthContext is not available in Profile.js");
    return <p>Error: AuthContext not found</p>;
  }

  return (
    <div className="profile-container d-flex justify-content-center align-items-center vh-100 bg-light">
      <div className="profile-card card shadow p-4" style={{ width: "24rem" }}>
        <h2 className="profile-title card-title text-center text-primary">User Profile</h2>
        {error ? (
          <p className="text-danger text-center">{error}</p>
        ) : userData ? (
          <ul className="profile-list list-group list-group-flush">
            <li className="list-group-item"><strong>User ID:</strong> {userData.userId}</li>
            <li className="list-group-item"><strong>Name:</strong> {userData.name}</li>
            <li className="list-group-item"><strong>Email:</strong> {userData.email}</li>
            <li className="list-group-item"><strong>Mobile:</strong> {userData.mobileNumber}</li>
            <li className="list-group-item"><strong>Address:</strong> {userData.address || "N/A"}</li>
            <li className="list-group-item"><strong>Membership:</strong> {userData.member ? "Active" : "Inactive"}</li>
          </ul>
        ) : (
          <p className="text-center text-secondary">Loading...</p>
        )}
      </div>
    </div>
  );
};

export default Profile;
