import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import "../styles/ProductList.css";
import AuthContext from "../AuthContext";

const ProductList = () => {
    const [products, setProducts] = useState([]);
    const [category, setCategory] = useState("");
    const [subcategory, setSubcategory] = useState("");
    const [productName, setProductName] = useState("");
    const [message, setMessage] = useState(""); // Message for alerts
    const { user } = useContext(AuthContext); // Get logged-in user
    const userId = user?.userId; // Get user ID from context

    useEffect(() => {
        fetchProducts();
    }, []);

    // Fetch all products
    const fetchProducts = async () => {
        try {
            const response = await axios.get("http://localhost:8080/api/products");
            setProducts(Array.isArray(response.data) ? response.data : []);
        } catch (error) {
            console.error("Error fetching products:", error);
            setProducts([]);
        }
    };

    // ✅ Add product to cart
    const addToCart = async (productId) => {
        if (!userId) {
            setMessage("⚠️ Please login to add items to the cart.");
            return;
        }

        try {
            const response = await axios.post(
                `http://localhost:8080/api/cart/add`,
                null, // No request body, sending parameters in the URL
                {
                    params: {
                        userId: userId,
                        productId: productId,
                        quantity: 1, // Default quantity
                    },
                }
            );

            if (response.status === 200) {
                setMessage("✅ Product added to cart successfully!");
            }
        } catch (error) {
            console.error("Error adding to cart:", error);
            setMessage("❌ Failed to add product to cart.");
        }
    };

    return (
        <div className="product-container">
            <h2>Product List</h2>

            {/* Display alert message */}
            {message && <p className="alert-message">{message}</p>}

            <div className="filters">
                <input
                    type="text"
                    placeholder="Filter by Category"
                    value={category}
                    onChange={(e) => setCategory(e.target.value)}
                />
                <button onClick={() => fetchProducts()}>Search</button>

                <input
                    type="text"
                    placeholder="Filter by Subcategory"
                    value={subcategory}
                    onChange={(e) => setSubcategory(e.target.value)}
                />
                <button onClick={() => fetchProducts()}>Search</button>

                <input
                    type="text"
                    placeholder="Filter by Product Name"
                    value={productName}
                    onChange={(e) => setProductName(e.target.value)}
                />
                <button onClick={() => fetchProducts()}>Search</button>
            </div>

            {/* Product Grid */}
            <div className="product-grid">
                {products.length > 0 ? (
                    products.map((product, index) => (
                        <div className="product-card" key={product.id || product.product_id || index}>
                            <img
                                src={product.image || "https://via.placeholder.com/150"}
                                alt={product.name || "Product Image"}
                                className="product-image"
                            />
                            <div className="product-info">
                                <h3>{product.name || product.product_name}</h3>
                                <p className="product-description">{product.description}</p>
                                <p className="product-price">₹{product.price}</p>
                                <div className="product-buttons">
                                    <button className="add-to-cart" onClick={() => addToCart(product.id || product.product_id)}>
                                        Add to Cart
                                    </button>
                                    <button className="buy-now">Buy Now</button>
                                </div>
                            </div>
                        </div>
                    ))
                ) : (
                    <p className="no-products">No products found</p>
                )}
            </div>
        </div>
    );
};

export default ProductList;
