import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Carousel } from "react-bootstrap";
import Header from "./Header";
import Footer from "./Footer";
import "../styles/Dashboard.css";

const Dashboard = () => {
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    setTimeout(() => {
      setIsLoading(false);
    }, 2000);
  }, []);

  return (
    <div>
      {isLoading && (
        <div className="preloader">
          <div className="spinner"></div>
        </div>
      )}

      <Header />

      <div className="hero-section">
        <Carousel interval={3000} pause={false} fade>
          <Carousel.Item>
            <img
              src="https://media.istockphoto.com/id/1292443598/photo/flying-shopping-cart-with-shopping-bags-on-a-pink-background.jpg"
              className="d-block w-100"
              alt="Shopping Banner 1"
            />
          </Carousel.Item>
          <Carousel.Item>
            <img
              src="https://images.unsplash.com/photo-1540221652346-e5dd6b50f3e7"
              className="d-block w-100"
              alt="Shopping Banner 2"
            />
          </Carousel.Item>
          <Carousel.Item>
            <img
              src="https://images.unsplash.com/photo-1550009158-9ebf69173e03"
              className="d-block w-100"
              alt="Shopping Banner 3"
            />
          </Carousel.Item>
        </Carousel>
      </div>

      <Footer />
    </div>
  );
};

export default Dashboard;
