import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles/ProductList.css";
import AuthContext from "../AuthContext";

const ProductList = () => {
    const [products, setProducts] = useState([]);
    const [message, setMessage] = useState("");
    const { user } = useContext(AuthContext);
    const userId = user?.userId;
    const [isMember, setIsMember] = useState(false);
    const [superCoins, setSuperCoins] = useState(0);
    const navigate = useNavigate();

    useEffect(() => {
        fetchProducts();
        if (userId) {
            checkMembershipStatus();
            fetchSuperCoins();
        }
    }, [userId]);

    const fetchProducts = async () => {
        try {
            const response = await axios.get("http://localhost:8080/api/products");
            console.log("Fetched Products Data:", response.data);  // Debugging
            setProducts(Array.isArray(response.data) ? response.data : []);
        } catch (error) {
            console.error("Error fetching products:", error);
            setProducts([]);
        }
    };

    const checkMembershipStatus = async () => {
        try {
            const response = await axios.get(`http://localhost:8080/users/${userId}`);
            console.log("User Membership Status:", response.data);  // Debugging
            setIsMember(response.data?.member || false);
        } catch (error) {
            console.error("Error checking membership status:", error);
        }
    };

    const fetchSuperCoins = async () => {
        try {
            const response = await axios.get(`http://localhost:8080/api/users/${userId}`);
            console.log("Fetched SuperCoins Data:", response.data);  // Debugging
            setSuperCoins(response.data?.superCoins || 0);
        } catch (error) {
            console.error("Error fetching SuperCoins:", error);
        }
    };

    const addToCart = async (productId, quantity = 1) => {
        if (!userId) {
            setMessage("âš  Please login to add items to the cart.");
            return;
        }

        try {
            const response = await axios.post(
                `http://localhost:8080/api/cart/add`,
                null,
                { params: { userId, productId, quantity } }
            );

            console.log("Add to Cart Response:", response.data);  // Debugging

            if (response.status === 200) {
                setMessage("âœ… Product added to cart successfully!");
            }
        } catch (error) {
            console.error("Error adding to cart:", error);
            setMessage("âŒ Failed to add product to cart.");
        }
    };

    const handleBuyNow = (productId) => {
        console.log("Navigating to product details:", productId);  // Debugging
        navigate(`/product/${productId}`);
    };

    return (

        <div className="product-container">


            <table className="product-table">
                <thead>
                    <tr>
                        <th className="product-header">Products</th>
                    </tr>
                <tbody>
                    {products.length > 0 ? (
                        products.reduce((rows, product, index) => {
                            if (index % 2 === 0) rows.push([]); // Change to 2 per row
                            rows[rows.length - 1].push(product);
                            return rows;
                        }, []).map((row, rowIndex) => (
                            <tr key={rowIndex}>
                                {row.map((product) => {
                                    const discountedPrice = (product.price * 0.9).toFixed(2);
                                    const coinDiscount = Math.min(superCoins, 50);
                                    const priceWithCoins = (product.price - coinDiscount).toFixed(2);

                                    return (
                                        <td key={product.productId} className="product-card">
                                            <img
                                                src={product.producturl || "https://via.placeholder.com/150"}
                                                alt={product.name || "Product Image"}
                                                className="product-image"
                                            />
                                            <div className="product-info">
                                                <h3>{product.productName}</h3>
                                                <p className="product-description">{product.description}</p>

                                                <p className="product-price">
                                                    {isMember ? (
                                                        <>
                                                        <label for = "og">Original Price: </label>
                                                            <span className="strikethrough" name="og">â‚¹{product.price}</span>
                                                            <br />
                                                            <label for = "di">Discounted Price: </label>
                                                            <span className="discount-price" name="di">â‚¹{discountedPrice}</span>
                                                        </>
                                                    ) : (
                                                        <>
                                                          <label for = "og">Original Price: </label>
                                                            <span className="" name="og">â‚¹{product.price}</span>
                                                            <br />
                                                            <label for = "di">Discounted Price: </label>
                                                            <span className="strikethrough" name="di"> â‚¹{discountedPrice}</span><br>
                                                            </br>
                                                            <span className="non-member-text"> (For Members Only)</span>
                                                        </>
                                                    )}
                                                </p>

                                                {superCoins > 0 && (
                                                    <p className="supercoins-offer">
                                                        ðŸ”¥ Buy with <b>{coinDiscount} SuperCoins</b> + <b>â‚¹{priceWithCoins}</b>
                                                    </p>
                                                )}

                                                <div className="product-buttons">
                                                    <button className="add-to-cart" onClick={() => addToCart(product.productId, 1)}>
                                                        Add to Cart
                                                    </button>
                                                    <button className="buy-now" onClick={() => handleBuyNow(product.productId)}>
                                                        Buy Now
                                                    </button>
                                                </div>
                                            </div>
                                        </td>
                                    );
                                })}
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="2" className="no-products">No products found</td> 
                        </tr>
                    )}
                </tbody>
                </thead>
            </table>
        </div>
    );
};

export default ProductList;
