import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles/ProductList.css";
import AuthContext from "../AuthContext";

const ProductList = () => {
    const navigate = useNavigate();
    const { user } = useContext(AuthContext);
    const userId = user?.userId;

    const [productState, setProductState] = useState({
        products: [],
        isMember: false,
        superCoins: 0,
        useSuperCoins: {},
        message: "",
    });

    useEffect(() => {
        fetchProducts();
        if (userId) {
            checkMembershipStatus();
            fetchSuperCoins();
        }
    }, [userId]);

    const fetchProducts = async () => {
        try {
            const response = await axios.get("http://localhost:8080/api/products");
            setProductState((prevState) => ({
                ...prevState,
                products: Array.isArray(response.data) ? response.data : [],
            }));
        } catch (error) {
            console.error("Error fetching products:", error);
            setProductState((prevState) => ({ ...prevState, products: [] }));
        }
    };

    const checkMembershipStatus = async () => {
        try {
            const response = await axios.get(`http://localhost:8080/users/${userId}`);
            setProductState((prevState) => ({
                ...prevState,
                isMember: response.data?.member || false,
            }));
        } catch (error) {
            console.error("Error checking membership status:", error);
        }
    };

    const fetchSuperCoins = async () => {
        try {
            const response = await axios.get(`http://localhost:8080/api/supercoins/${userId}`);
            setProductState((prevState) => ({
                ...prevState,
                superCoins: response.data?.balance || 0,
            }));
        } catch (error) {
            console.error("Error fetching SuperCoins:", error);
            setProductState((prevState) => ({ ...prevState, superCoins: 0 }));
        }
    };

    const addToCart = async (productId, quantity = 1) => {
        if (!userId) {
            setProductState((prevState) => ({
                ...prevState,
                message: "⚠ Please login to add items to the cart.",
            }));
            return;
        }
        try {
            const response = await axios.post(
                "http://localhost:8080/api/cart/add",
                null,
                { params: { userId, productId, quantity } }
            );
            if (response.status === 200) {
                const { cartId } = response.data;
                setProductState((prevState) => ({
                    ...prevState,
                    message: "✅ Product added to cart successfully!",
                }));
                let storedCart = JSON.parse(localStorage.getItem("cart")) || [];
                storedCart.push({ cartId, productId, quantity });
                localStorage.setItem("cart", JSON.stringify(storedCart));
            }
        } catch (error) {
            console.error("Error adding to cart:", error);
            setProductState((prevState) => ({
                ...prevState,
                message: "❌ Failed to add product to cart.",
            }));
        }
    };

    const toggleSuperCoins = (productId) => {
        setProductState((prevState) => ({
            ...prevState,
            useSuperCoins: {
                ...prevState.useSuperCoins,
                [productId]: !prevState.useSuperCoins[productId],
            },
        }));
    };

    return (
        <>
            {/* Heading */}
            <div className="product-container">
                <h1>Products</h1>
            </div>

            {/* Message Container */}
            {productState.message && (
                <div className="product-container alert-message" style={{ color: "red" }}>
                    <p>{productState.message}</p>
                </div>
            )}

            {/* Product Grid */}
            <div className="product-container">
                <div className="product-grid">
                    {productState.products.length > 0 ? (
                        productState.products.map((product) => {
                            const discountedPrice = (product.price * 0.9).toFixed(2);
                            const coinDiscount = productState.superCoins > 0 ? Math.min(productState.superCoins, 50) : 50;
                            const isChecked = productState.useSuperCoins[product.productId] || false;
                            const finalPrice = (productState.isMember
                                ? (discountedPrice - (isChecked ? coinDiscount : 0)).toFixed(2)
                                : (product.price - (isChecked ? coinDiscount : 0)).toFixed(2)
                            );

                            return (
                                <div className="product-card" key={product.productId}>
                                    <img
                                        src={product.producturl || "https://via.placeholder.com/300"}
                                        alt={product.name || "Product Image"}
                                        className="product-image"
                                    />
                                    <div className="product-info">
                                        <h3>{product.name}</h3>
                                        <p className="product-description">{product.description}</p>

                                        {/* Pricing Section */}
                                        <div className="product-pricing">
                                            <p className="price-label"><strong>Original Price:</strong></p>
                                            <p className="original-price">
                                                <span>MRP: </span>
                                                {productState.isMember ? <del>₹{product.price}</del> : `₹${product.price}`}
                                            </p>

                                            <p className="price-label"><strong>Discounted Price:</strong></p>
                                            <p className="discount-price">
                                                <span>🏷 Discounted Price: </span>
                                                {productState.isMember ? `₹${discountedPrice}` : <del>₹{discountedPrice}</del>}
                                            </p>

                                            {/* SuperCoins Option */}
                                            <div className="supercoin-container">
                                                <input
                                                    type="checkbox"
                                                    id={`useSuperCoins-${product.productId}`}
                                                    checked={isChecked}
                                                    onChange={() => toggleSuperCoins(product.productId)}
                                                />
                                                <label htmlFor={`useSuperCoins-${product.productId}`}>
                                                    💰 Use {coinDiscount} SuperCoins
                                                </label>
                                            </div>

                                            {/* Final Price */}
                                            <p className="final-price">
                                                <span>⚡ Final Price: </span>
                                                <span className="highlight-price">₹{finalPrice}</span>
                                            </p>
                                        </div>

                                        {/* Buttons */}
                                        <div className="product-buttons">
                                            <button className="add-to-cart" onClick={() => addToCart(product.productId, 2)}>
                                                Add to Cart
                                            </button>
                                            <button
                                                className="btn btn-primary"
                                                onClick={() => navigate(`/product/${product.productId}`)}
                                            >
                                                Buy Now
                                            </button>
                                        </div>
                                    </div>
                                </div>
                            );
                        })
                    ) : (
                        <p className="no-products">No products found</p>
                    )}
                </div>
            </div>
        </>
    );
};

export default ProductList;
