import { useState, useContext } from "react";
import { loginUser } from "../api";
import AuthContext from "../AuthContext";
import { useNavigate } from "react-router-dom";
import "../styles/Auth.css";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const { login } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await loginUser(email, password);

      console.log("Login response:", response.data); // ✅ Debugging Log

      // Make sure the backend returns a user object with `userId`
      login({ userId: response.data.userId, email: response.data.email }, response.data.token);

      navigate("/dashboard");
    } catch (error) {
      console.error("Login Error:", error);
      alert("Login Failed! Please check your credentials.");
    }
  };

  return (
    <div className="auth-container">
      <h2>Login</h2>
      <form className="auth-form" onSubmit={handleSubmit}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit" className="auth-btn">Login</button>
        <button type="button" className="auth-btn secondary" onClick={() => navigate("/register")}>
          Sign Up
        </button>
      </form>
    </div>
  );
};

export default Login;
