import React, { useState, useContext } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "../styles/ProductDetails.css";
import AuthContext from "../AuthContext";

const ProductDetail = () => {
  const { id } = useParams();
  const { user, productState } = useContext(AuthContext); // Access productState from context
  const userId = user?.userId;

  // ‚úÖ Fix: Ensure `productState` exists before accessing `products`
  const products = productState?.products || [];
  const product = products.find((p) => p.productId.toString() === id);

  const [message, setMessage] = useState("");
  const [cartLoading, setCartLoading] = useState(false);

  if (!productState) {
    return <div className="loading-message">‚è≥ Loading product details...</div>;
  }

  if (!product) {
    return <div className="error-message">‚ùå Product not found.</div>;
  }

  // Function to handle Add to Cart
  const addToCart = async () => {
    if (!userId) {
      setMessage("‚ö† Please log in to add items to your cart.");
      return;
    }

    setCartLoading(true);
    setMessage("");

    try {
      const response = await axios.post("http://localhost:8080/api/cart/add", null, {
        params: { userId, productId: id, quantity: 1 },
      });

      if (response.status === 200) {
        setMessage("‚úÖ Product added to cart successfully!");
      }
    } catch (error) {
      setMessage("‚ùå Failed to add product to cart.");
    } finally {
      setCartLoading(false);
    }
  };

  return (
    <div className="product-detail">
      <div className="product-container">
        <div className="image-container">
          <img
            src={product.producturl || "https://via.placeholder.com/300"}
            alt={product.name}
            className="product-image fade-in"
          />
        </div>

        <div className="product-info">
          <h1 className="product-name">{product.name}</h1>
          <p className="product-category">
            Category: <span>{product.category?.categoryName || "Uncategorized"}</span>
          </p>
          <p className="product-price">‚Çπ{parseFloat(product.price).toFixed(2)}</p>
          <p className="product-description">{product.description}</p>

          <div className="buttons">
            <button className="buy-now-btn">Buy Now</button>
            <button className="add-to-cart-btn" onClick={addToCart} disabled={cartLoading}>
              {cartLoading ? "Adding..." : "Add to Cart"}
            </button>
          </div>

          <p className="delivery-info">
            üöö Free delivery by <strong>Tomorrow</strong> if ordered today.
          </p>

          {message && <p className="alert-message">{message}</p>}
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
