import React, { useState } from "react";
import "../styles/Categories.css";

const categoryData = [
  { name: "Electronics", img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTGd64suWg7FlHc74w8K0EX0C0XCCgKDNnsmw&s" },
  { name: "Fashion", img: "https://www.shutterstock.com/image-photo/mobile-shopping-cheerful-black-woman-600nw-1963546744.jpg" },
  { name: "Home Appliances", img: "https://t3.ftcdn.net/jpg/03/29/32/18/360_F_329321873_tWphXnKQHwtZzj4xHUvFRsnaL6cEoLtX.jpg" },
  { name: "Sports", img: "https://t3.ftcdn.net/jpg/00/71/65/34/360_F_71653410_HQRC2EsrZOsP28iIWz5bhQx9EUQOgcan.jpg" },
  { name: "Beauty & Health", img: "https://media.istockphoto.com/id/1141698953/photo/spa-products-for-home-skin-care.jpg?s=612x612&w=0&k=20&c=HxtIt73MwCZBY0APYngv0poZCEtyDhckTuT8SxJSxPE=" },
  { name: "Toys & Games", img: "https://www.shukanmall.com/product-img/thumb/Bubble-Blowing-Santa-Flasing-L-1725520014.jpg" },
  { name: "Groceries", img: "https://blog.grabon.in/wp-content/uploads/2020/10/Spar.jpg" },
  { name: "Furniture", img: "https://images.woodenstreet.de/image/data/store%20page/new/andheri%20west/new/new/4.jpg" },
];

const Categories = () => {
  const [hoveredCategory, setHoveredCategory] = useState(null);

  return (
    <div className="categories">
      <h2 className="section-title">Shop By Categories</h2>
      <div className="categories-grid">
        {categoryData.map((category, index) => (
          <div
            key={index}
            className={`category-item ${hoveredCategory === index ? "hovered" : ""}`}
            onMouseEnter={() => setHoveredCategory(index)}
            onMouseLeave={() => setHoveredCategory(null)}
          >
            <img src={category.img} alt={category.name} />
            <p>{category.name}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Categories;
