import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "../styles/SubSubCategories.css";  
import "bootstrap/dist/css/bootstrap.min.css";

const SubSubcategories = () => {
  const { id } = useParams(); // Extracting id from URL
  const [subSubcategories, setSubSubcategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchSubSubcategories = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/subsubcategories/${id}`);
        setSubSubcategories(response.data);
      } catch (err) {
        setError("Error fetching sub-subcategories");
      } finally {
        setLoading(false);
      }
    };

    fetchSubSubcategories();
  }, [id]);

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="container">
      <h2 className="text-center my-4">Sub-Subcategories</h2>
      <div className="row">
        {subSubcategories.length > 0 ? (
          subSubcategories.map((product) => (
            <div key={product.id} className="col-md-4 col-sm-6 mb-4">
              <div className="product-card">
                <img 
                  src={product.imageUrl || "https://via.placeholder.com/150"} 
                  alt={product.name} 
                  className="product-image" 
                />
                <div className="product-info">
                  <h4>{product.name}</h4>
                  <p>{product.description}</p>
                  <p className="price">â‚¹{product.price}</p>
                  <button className="btn btn-primary">Buy Now</button>
                </div>
              </div>
            </div>
          ))
        ) : (
          <p className="text-center">No sub-subcategories found.</p>
        )}
      </div>
    </div>
  );
};

export default SubSubcategories;
