import React, { useContext } from "react";
import { Link, useNavigate } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import { PersonCircle } from "react-bootstrap-icons"; // Bootstrap profile icon
import "../styles/Header.css";
import AuthContext from "../AuthContext";

const Header = () => {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate("/");
  };

  return (
    <nav className="navbar navbar-expand-lg fixed-top shadow-sm">
      <div className="container-fluid">
        {/* Logo Image */}
        <Link className="navbar-brand" to="/dashboard">
          <img
            src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSF8ExH1zQPwZ4z3pa4gdCl76l9eJ4T7Ai8iw&s"
            alt="E-Mart Logo"
            className="logo-img"
          />
        </Link>

        <button className="navbar-toggler" type="button" data-bs-toggle="collapse"
          data-bs-target="#navbarNav" aria-controls="navbarNav"
          aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>

        <div className="collapse navbar-collapse justify-content-between" id="navbarNav">
          <ul className="navbar-nav mx-auto">
            <li className="nav-item"><Link className="nav-link" to="/dashboard">Home</Link></li>
            <li className="nav-item"><Link className="nav-link" to="/productlist">Products</Link></li>
            <li className="nav-item"><Link className="nav-link" to="/categorylist">Categories</Link></li>
            <li className="nav-item"><Link className="nav-link" to="/orders">Orders</Link></li>
            <li className="nav-item"><Link className="nav-link" to="/contact">Contact</Link></li>
          </ul>

          <div className="auth-section">
            {user ? (
              <div className="d-flex align-items-center">
                <Link to="/profile" className="profile-icon">
                  <PersonCircle size={24} />
                </Link>
                <button className="btn btn-danger fw-bold ms-3" onClick={handleLogout}>Logout</button>
              </div>
            ) : (
              <button className="btn btn-light fw-bold" onClick={() => navigate("/login")}>Login</button>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Header;
