import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import AuthContext from "../AuthContext";
import axios from "axios";
import { Form, Button, Card } from "react-bootstrap";
import { toast } from "react-toastify";
import "../styles/Payment.css";

const PaymentPage = () => {
  const { user, token } = useContext(AuthContext);
  const userId = user?.userId;
  const navigate = useNavigate();

  const [userDetails, setUserDetails] = useState(null);
  const [paymentMethod, setPaymentMethod] = useState("");
  const [orderDetails, setOrderDetails] = useState([]);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!userId) return;

    const fetchData = async () => {
      try {
        // Fetch user details
        const userResponse = await axios.get(`http://localhost:8080/users/${userId}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUserDetails(userResponse.data);

        // Fetch orders
        const ordersResponse = await axios.get(
          `http://localhost:8080/api/orders/user/${userId}`,
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        const orders = ordersResponse.data;

        if (orders.length === 0) {
          toast.error("No orders found.");
          setLoading(false);
          return;
        }

        // Fetch order details for the first order
        const orderId = orders[0].orderId;
        const orderDetailsResponse = await axios.get(
          `http://localhost:8080/api/orderdetails/order/${orderId}`,
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        const orderDetailsData = orderDetailsResponse.data;
        setOrderDetails(orderDetailsData);

        // Fetch product details
        const productRequests = orderDetailsData.map((item) =>
          axios.get(`http://localhost:8080/api/products/${item.productId}`, {
            headers: { Authorization: `Bearer ${token}` },
          })
        );

        const productResponses = await Promise.all(productRequests);
        const productsData = productResponses.map((res) => res.data);
        setProducts(productsData);
      } catch (error) {
        toast.error("Failed to fetch data.");
        console.error("Error:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [userId, token]);

  const handlePayment = async () => {
    if (!paymentMethod) {
      toast.error("Please select a payment method.");
      return;
    }

    const paymentData = {
      userId,
      paymentMethod,
      amount: orderDetails.reduce((sum, item) => sum + item.totalPrice, 0),
      status: "Completed",
    };

    try {
      await axios.post("http://localhost:8080/api/paymentdetails/$id", paymentData, {
        headers: { Authorization: `Bearer ${token}` },
      });

      toast.success("Payment successful!");
      navigate("/order-confirmed");
    } catch (error) {
      toast.error("Payment failed.");
      console.error("Error processing payment:", error);
    }
  };

  return (
    <div className="payment-container">
      <h2 className="payment-title">üí≥ Payment Page</h2>

      {loading ? (
        <p>Loading payment details...</p>
      ) : (
        <>
          {userDetails ? (
            <div className="payment-form">
              <h4>User Details</h4>
              <Form>
                <Form.Group>
                  <Form.Label>Name</Form.Label>
                  <Form.Control type="text" value={userDetails.name} readOnly />
                </Form.Group>
                <Form.Group>
                  <Form.Label>Address</Form.Label>
                  <Form.Control type="text" value={userDetails.address} readOnly />
                </Form.Group>
                <Form.Group>
                  <Form.Label>Email</Form.Label>
                  <Form.Control type="email" value={userDetails.email} readOnly />
                </Form.Group>
                <Form.Group>
                  <Form.Label>Mobile</Form.Label>
                  <Form.Control type="text" value={userDetails.mobileNumber} readOnly />
                </Form.Group>
                <Form.Group>
                  <Form.Label>Payment Method</Form.Label>
                  <Form.Control
                    as="select"
                    value={paymentMethod}
                    onChange={(e) => setPaymentMethod(e.target.value)}
                  >
                    <option value="">Select Payment Method</option>
                    <option value="Cash on Delivery">Cash on Delivery</option>
                    <option value="Credit Card">Credit Card</option>
                    <option value="UPI">UPI</option>
                  </Form.Control>
                </Form.Group>
              </Form>
              <Button onClick={handlePayment} className="pay-button">
                Proceed to Pay
              </Button>
            </div>
          ) : (
            <p>No user details found.</p>
          )}

          <h4 className="product-title">üõç Products in Order</h4>
          <div className="product-list">
            {products.length > 0 ? (
              products.map((product, index) => (
                <Card key={product.id} className="product-card">
                  <Card.Img variant="top" src={product.image} className="product-image" />
                  <Card.Body>
                    <Card.Title>{product.name}</Card.Title>
                    <Card.Text>{product.description}</Card.Text>
                    <p>
                      <strong>Quantity:</strong> {orderDetails[index]?.quantity || 1}
                    </p>
                    <p>
                      <strong>Price:</strong> ‚Çπ{orderDetails[index]?.totalPrice || product.price}
                    </p>
                  </Card.Body>
                </Card>
              ))
            ) : (
              <p>No products found.</p>
            )}
          </div>
        </>
      )}
    </div>
  );
};

export default PaymentPage;
