import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import AuthContext from "../AuthContext"; 
import "../styles/Profile.css"; 

const Profile = () => {
  const { user } = useContext(AuthContext); // Get the logged-in user
  const [userData, setUserData] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!user || !user.userId) {
      console.log("No user logged in or missing userId.");
      return;
    }

    console.log(`Fetching user data for ID: ${user.userId}`);

    const fetchUserProfile = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/users/${user.userId}`, {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }, // Send token
        });
        console.log("User data fetched successfully:", response.data);
        setUserData(response.data);
        setError(null);
      } catch (err) {
        console.error("Failed to fetch user profile:", err);
        setError("Failed to fetch user profile");
      }
    };

    fetchUserProfile();
  }, [user]);

  if (!user) {
    console.log("User not found in AuthContext.");
    return <div className="error-text">Please log in to view your profile.</div>;
  }

  if (error) {
    console.log("Error fetching user data:", error);
    return <div className="error-text">{error}</div>;
  }

  if (!userData) {
    console.log("Waiting for user data...");
    return <div className="loading-text">Loading...</div>;
  }

  return (
    <div className="profile-container">
      <div className="profile-card">
        <h2 className="profile-title">User Profile</h2>
        <ul className="profile-list">
          <li><strong>User ID:</strong> {userData.userId}</li>
          <li><strong>Name:</strong> {userData.name}</li>
          <li><strong>Email:</strong> {userData.email}</li>
          <li><strong>Mobile:</strong> {userData.mobileNumber}</li>
          <li><strong>Address:</strong> {userData.address || "N/A"}</li>
        </ul>
      </div>
    </div>
  );
};

export default Profile;
