import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Carousel } from "react-bootstrap";
import Header from "./Header";
import "../styles/Dashboard.css";

const Dashboard = () => {
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    setTimeout(() => {
      setIsLoading(false);
    }, 2000);
  }, []);

  return (
    <div>
      {isLoading && (
        <div className="preloader">
          <div className="spinner"></div>
        </div>
      )}

      <Header />

      {/* Hero Section with Infinite Loop Carousel */}
      <div className="hero-section">
        <Carousel interval={1000} controls={false} indicators={false} fade infiniteLoop>
          <Carousel.Item>
            <img
              src="https://imgs.search.brave.com/7NH5p7jrL7enSFmrTJpzpGDyS1jk5MOc91mb3_JsMdo/rs:fit:860:0:0:0/g:ce/aHR0cHM6Ly9pbWFn/ZXMtbmEuc3NsLWlt/YWdlcy1hbWF6b24u/Y29tL2ltYWdlcy9H/LzAxL2FkbHAvYnVp/bGRlci9jYXJvdXNl/bC14Ym94LXNtLTYy/MzdkMWMyLWRjNzUt/NDU3ZC05OWE0LWY0/NDAyYWFkNjk2Mi5q/cGc"
              className="d-block w-100"
              alt="Slide 1"
            />
          </Carousel.Item>
          <Carousel.Item>
            <img
              src="https://imgs.search.brave.com/bSagvueGywaeMQ9VcPO9JPAp6EaUSkFV_cy-uy6Kx8c/rs:fit:500:0:0:0/g:"
              className="d-block w-100"
              alt="Slide 2"
            />
          </Carousel.Item>
        </Carousel>
      </div>

      {/* Welcome Section */}
      <div className="container my-5 text-center">
        <h2>Welcome to E-Mart</h2>
        <p>Discover the latest trends in fashion, electronics, and more!</p>
        <a href="/productpage" className="btn btn-primary btn-lg">
          Start Shopping
        </a>
      </div>
    </div>
  );
};

export default Dashboard;
