import React, { useEffect, useState, useContext } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "../styles/ProductDetails.css";
import AuthContext from "../AuthContext"; // Import authentication context

const ProductDetail = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [error, setError] = useState(null);
  const [message, setMessage] = useState(""); // For success/error messages
  const { user } = useContext(AuthContext); // Get logged-in user info
  const userId = user?.userId; // Extract user ID from context

  console.log("ProductDetail component loaded");
  console.log("Product ID from URL:", id);
  console.log("User ID from context:", userId);

  useEffect(() => {
    if (!id) {
      console.error("No product ID found in URL");
      return;
    }

    console.log(`Fetching product details for ID: ${id}`);

    axios
      .get(`http://localhost:8080/api/products/${id}`)
      .then((response) => {
        console.log("API Response:", response);
        if (response.data) {
          console.log("Product Data Received:", response.data);
          setProduct(response.data);
        } else {
          console.error("Empty response received from API");
          setError("Product not found.");
        }
      })
      .catch((error) => {
        console.error("Error fetching product details:", error);
        setError("Failed to load product details. Please try again.");
      });
  }, [id]);

  // ✅ Add product to cart
  const addToCart = async () => {
    if (!userId) {
      console.warn("User is not logged in. Cannot add to cart.");
      setMessage("⚠️ Please log in to add items to your cart.");
      return;
    }

    console.log(`Adding product ${id} to cart for user ${userId}`);

    try {
      const response = await axios.post(
        "http://localhost:8080/api/cart/add",
        null,
        {
          params: { userId, productId: id, quantity: 1 },
        }
      );

      console.log("Add to Cart Response:", response);

      if (response.status === 200) {
        console.log("Product added to cart successfully");
        setMessage("✅ Product added to cart successfully!");
      }
    } catch (error) {
      console.error("Error adding to cart:", error);
      setMessage("❌ Failed to add product to cart.");
    }
  };

  if (error) {
    console.log("Displaying error message:", error);
    return <div className="error-message">{error}</div>;
  }

  if (!product) {
    console.log("Loading product details...");
    return <div className="loading">Loading product details...</div>;
  }

  return (
    <div className="product-detail">
      <div className="product-container">
        <div className="image-container">
          <img
            src={product.imageUrl || "https://via.placeholder.com/300"}
            alt={product.productName}
            className="product-image"
          />
        </div>
        <div className="product-info">
          <h1 className="product-name">{product.productName}</h1>
          <p className="product-category">
            Category: <span>{product.category?.categoryName || "Uncategorized"}</span>
          </p>
          <p className="product-price">₹{parseFloat(product.price).toFixed(2)}</p>
          <p className="product-description">{product.description}</p>
          <div className="buttons">
            <button className="buy-now-btn">Buy Now</button>
            <button className="add-to-cart-btn" onClick={addToCart}>Add to Cart</button>
          </div>
          <p className="delivery-info">
            Free delivery by <strong>Tomorrow</strong> if ordered today.
          </p>

          {/* Show success/error message */}
          {message && <p className="alert-message">{message}</p>}
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
