import React, { useEffect, useState } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import "../styles/Footer.css";

const Footer = () => {
  const [location, setLocation] = useState({ city: "Unknown", country: "Unknown" });
  const [time, setTime] = useState(new Date().toLocaleTimeString());

  // Fetch user location
  useEffect(() => {
    axios.get("https://ipapi.co/json/")
      .then(response => setLocation({ city: response.data.city, country: response.data.country_name }))
      .catch(error => console.error("Location fetch failed:", error));
  }, []);

  // Update time every second
  useEffect(() => {
    const timer = setInterval(() => setTime(new Date().toLocaleTimeString()), 1000);
    return () => clearInterval(timer);
  }, []);

  return (
    <footer className="footer bg-dark text-light py-3">
      <div className="container text-center">
        <div className="row">
          {/* Left - About Us & Contact */}
          <div className="col-md-4 text-start">
            <h5>About Us</h5>
            <p><a href="/about" className="footer-link">Learn More</a></p>
            <p>Contact: support@emart.com</p>
            <p>Toll-Free: 1800-123-4567</p>
          </div>

          {/* Center - Address & Rights */}
          <div className="col-md-4 text-center">
            <h5>Our Address</h5>
            <p>123 E-Mart Street, Commerce City, USA</p>
            <p>Â© 2025 Unstoppable GRP3. All rights reserved.</p>
          </div>

          {/* Right - User Location & Time */}
          <div className="col-md-4 text-end">
            <h5>Your Location</h5>
            <p>{location.city}, {location.country}</p>
            <h5>Current Time</h5>
            <p>{time}</p>
          </div>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
