import React, { useState, useEffect } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import "../styles/Product.css"; // Ensure styling is included

const ProductBySubcategory = () => {
  const [products, setProducts] = useState([]);
  const [subcategory, setSubcategory] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    if (subcategory) {
      fetchBySubcategory();
    }
  }, [subcategory]); // Fetch when subcategory changes

  const fetchBySubcategory = async () => {
    if (!subcategory) return;
    setLoading(true);
    setError("");

    try {
      const response = await axios.get(
        `http://localhost:8080/api/products/subcategory/${subcategory}`
      );
      setProducts(response.data);
    } catch (error) {
      console.error("Error fetching products by subcategory:", error);
      setError("Failed to fetch products. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="product-container">
      <h2>Products by Subcategory</h2>

      <div className="filter-section">
        <input
          type="text"
          placeholder="Enter Subcategory Name"
          value={subcategory}
          onChange={(e) => setSubcategory(e.target.value)}
          className="search-input"
        />
        <button onClick={fetchBySubcategory} className="search-btn">
          Search
        </button>
      </div>

      {loading && <p>Loading products...</p>}
      {error && <p className="error-text">{error}</p>}

      <table className="product-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Description</th>
            <th>Price</th>
            <th>Stock</th>
            <th>Category ID</th>
            <th>Subcategory ID</th>
          </tr>
        </thead>
        <tbody>
          {products.length > 0 ? (
            products.map((product) => (
              <tr key={product.product_id || product.id}>
                <td>{product.product_id || product.id}</td>
                <td>{product.product_name || product.name}</td>
                <td>{product.description}</td>
                <td>${product.price}</td>
                <td>{product.stock}</td>
                <td>{product.category_id}</td>
                <td>{product.subcategory_id}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="7" className="no-products">
                No products found.
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default ProductBySubcategory;
