import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import AuthContext from "../AuthContext";
import { Button, Form, Modal } from "react-bootstrap";
import { toast } from "react-toastify";

const PaymentPage = () => {
  const { user, token } = useContext(AuthContext);
  const userId = user?.userId;
  const navigate = useNavigate();

  const [paymentDetails, setPaymentDetails] = useState(null);
  const [userDetails, setUserDetails] = useState(null);
  const [paymentMethod, setPaymentMethod] = useState("Cash on Delivery");
  const [showModal, setShowModal] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (userId) {
      fetchPaymentDetails();
      fetchUserDetails();
    }
  }, [userId]);

  const fetchPaymentDetails = async () => {
    try {
      const response = await axios.get(
        `http://localhost:8081/api/paymentdetails/${userId}`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setPaymentDetails(response.data);
    } catch (error) {
      toast.error("Error fetching payment details.");
    }
  };

  const fetchUserDetails = async () => {
    try {
      const response = await axios.get(
        `http://localhost:8081/users/${userId}`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setUserDetails(response.data);
      setLoading(false);
    } catch (error) {
      toast.error("Error fetching user details.");
      setLoading(false);
    }
  };

  const handlePayment = async () => {
    const paymentData = {
      userId,
      paymentMethod,
      amount: paymentDetails?.totalAmount,
      status: "Pending",
    };

    try {
      await axios.post(
        "http://localhost:8081/api/paymentdetails/",
        paymentData,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      toast.success("Payment initiated successfully!");
      navigate("/order-confirmation");
    } catch (error) {
      toast.error("Error processing payment.");
    }
  };

  const handleUserUpdate = async () => {
    try {
      await axios.put(
        `http://localhost:8081/users/${userId}`,
        userDetails,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      toast.success("User info updated!");
      setShowModal(false);
    } catch (error) {
      toast.error("Error updating user details.");
    }
  };

  return (
    <div className="payment-page-container">
      <h2 className="payment-title">💳 Payment Details</h2>

      {loading ? (
        <p>Loading payment details...</p>
      ) : paymentDetails && userDetails ? (
        <div className="payment-details-container">
          <div className="user-info">
            <h4>User Info</h4>
            <p>Name: {userDetails.name}</p>
            <p>Email: {userDetails.email}</p>
            <p>Phone: {userDetails.phone}</p>
            <p>Address: {userDetails.address}</p>
          </div>

          <div className="payment-summary">
            <h4>Payment Summary</h4>
            <p>
              <strong>Amount to Pay: ₹{paymentDetails.totalAmount}</strong>
            </p>
            <p>
              <strong>Order ID: {paymentDetails.orderId}</strong>
            </p>
          </div>

          <div className="payment-methods">
            <h4>Select Payment Method</h4>
            <Form>
              <Form.Check
                type="radio"
                label="Cash on Delivery"
                name="paymentMethod"
                value="Cash on Delivery"
                checked={paymentMethod === "Cash on Delivery"}
                onChange={() => setPaymentMethod("Cash on Delivery")}
              />
              <Form.Check
                type="radio"
                label="Online Payment"
                name="paymentMethod"
                value="Online Payment"
                checked={paymentMethod === "Online Payment"}
                onChange={() => setPaymentMethod("Online Payment")}
              />
            </Form>
          </div>

          <Button variant="primary" onClick={handlePayment}>
            Proceed to Pay
          </Button>

          <Button variant="secondary" onClick={() => setShowModal(true)}>
            Modify User Info
          </Button>

          {/* Modal for modifying user info */}
          <Modal show={showModal} onHide={() => setShowModal(false)}>
            <Modal.Header closeButton>
              <Modal.Title>Modify User Information</Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <Form>
                <Form.Group className="mb-3">
                  <Form.Label>Name</Form.Label>
                  <Form.Control
                    type="text"
                    value={userDetails.name}
                    onChange={(e) =>
                      setUserDetails({ ...userDetails, name: e.target.value })
                    }
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>Email</Form.Label>
                  <Form.Control
                    type="email"
                    value={userDetails.email}
                    onChange={(e) =>
                      setUserDetails({ ...userDetails, email: e.target.value })
                    }
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>Phone</Form.Label>
                  <Form.Control
                    type="text"
                    value={userDetails.phone}
                    onChange={(e) =>
                      setUserDetails({ ...userDetails, phone: e.target.value })
                    }
                  />
                </Form.Group>
                <Form.Group className="mb-3">
                  <Form.Label>Address</Form.Label>
                  <Form.Control
                    type="text"
                    value={userDetails.address}
                    onChange={(e) =>
                      setUserDetails({ ...userDetails, address: e.target.value })
                    }
                  />
                </Form.Group>
              </Form>
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={() => setShowModal(false)}>
                Close
              </Button>
              <Button variant="primary" onClick={handleUserUpdate}>
                Save Changes
              </Button>
            </Modal.Footer>
          </Modal>
        </div>
      ) : (
        <p>Unable to fetch payment details.</p>
      )}
    </div>
  );
};

export default PaymentPage;
