import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles/ProductList.css";
import AuthContext from "../AuthContext";

const ProductList = () => {
    const [products, setProducts] = useState([]);
    const [message, setMessage] = useState("");
    const { user } = useContext(AuthContext);
    const userId = user?.userId;
    const [isMember, setIsMember] = useState(false);
    const [superCoins, setSuperCoins] = useState(0);
    const [useSuperCoins, setUseSuperCoins] = useState({});
    const navigate = useNavigate();

    useEffect(() => {
        fetchProducts();
        if (userId) {
            checkMembershipStatus();
            fetchSuperCoins();
        }
    }, [userId]);

    const fetchProducts = async () => {
        try {
            const response = await axios.get("http://localhost:8080/api/products");
            setProducts(Array.isArray(response.data) ? response.data : []);
        } catch (error) {
            console.error("Error fetching products:", error);
            setProducts([]);
        }
    };

    const checkMembershipStatus = async () => {
        try {
            const response = await axios.get(`http://localhost:8080/users/${userId}`);
            setIsMember(response.data?.member || false);
        } catch (error) {
            console.error("Error checking membership status:", error);
        }
    };

    const fetchSuperCoins = async () => {
        try {
            const response = await axios.get(`http://localhost:8080/api/users/${userId}`);
            setSuperCoins(response.data?.superCoins || 0);
        } catch (error) {
            console.error("Error fetching SuperCoins:", error);
        }
    };

    const toggleSuperCoinUsage = (productId) => {
        setUseSuperCoins((prev) => ({ ...prev, [productId]: !prev[productId] }));
    };

    return (
        <div className="product-container">
            <table className="product-table">
                <thead>
                    <tr>
                        <th className="product-header"><center><h1>Products</h1></center></th>
                    </tr>
                </thead>
                <tbody>
                    {message && <p className="alert-message">{message}</p>}
                    {products.length > 0 ? (
                        products.map((product) => {
                            const discountedPrice = (product.price * 0.9).toFixed(2);
                            const canUseSuperCoins = superCoins >= 100;
                            const finalPrice = useSuperCoins[product.productId] ? (product.price - 10).toFixed(2) : product.price;

                            return (
                                <tr key={product.productId} className="product-card">
                                    <td>
                                        <img
                                            src={product.producturl || "https://via.placeholder.com/150"}
                                            alt={product.name || "Product Image"}
                                            className="product-image"
                                        />
                                        <div className="product-info">
                                            <h3>{product.productName}</h3>
                                            <p className="product-description">{product.description}</p>
                                            <p className="product-price">
                                                {isMember ? (
                                                    <>
                                                        <label>Original Price: </label>
                                                        <span className={useSuperCoins[product.productId] ? "strikethrough" : ""}>₹{product.price}</span>
                                                        <br />
                                                        <label>Discounted Price: </label>
                                                        <span className={useSuperCoins[product.productId] ? "strikethrough" : "discount-price"}>₹{discountedPrice}</span>
                                                        {useSuperCoins[product.productId] && <span> → ₹{finalPrice} (Using 100 SuperCoins)</span>}
                                                    </>
                                                ) : (
                                                    <>
                                                        <label>Original Price: </label>
                                                        <span className={useSuperCoins[product.productId] ? "strikethrough" : ""}>₹{product.price}</span>
                                                        <br />
                                                        <label>Discounted Price: </label>
                                                        <span className={useSuperCoins[product.productId] ? "strikethrough" : ""}>₹{discountedPrice}</span>
                                                        {useSuperCoins[product.productId] && <span> → ₹{finalPrice} (Using 100 SuperCoins)</span>}
                                                    </>
                                                )}
                                            </p>
                                            {canUseSuperCoins && (
                                                <div>
                                                    <input
                                                        type="checkbox"
                                                        checked={useSuperCoins[product.productId] || false}
                                                        onChange={() => toggleSuperCoinUsage(product.productId)}
                                                    />
                                                    <label> Use 100 SuperCoins for ₹10 Off</label>
                                                </div>
                                            )}
                                        </div>
                                    </td>
                                </tr>
                            );
                        })
                    ) : (
                        <tr>
                            <td colSpan="2" className="no-products">No products found</td>
                        </tr>
                    )}
                </tbody>
            </table>
        </div>
    );
};

export default ProductList;
