import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import AuthContext from "../AuthContext";
import "../styles/Cart.css";

const Cart = () => {
    const { user } = useContext(AuthContext);
    const userId = user?.userId;

    const [cartItems, setCartItems] = useState([]);
    const [message, setMessage] = useState("");
    const [isMember, setIsMember] = useState(false); // Default false

    useEffect(() => {
        if (userId) {
            fetchCartItems();
            checkMembershipStatus();
        }
    }, [userId]);

    // ‚úÖ Fetch Cart Items
    const fetchCartItems = async () => {
        try {
            const response = await axios.get(`http://localhost:8080/api/cart/user/${userId}`);
            setCartItems(response.data);
        } catch (error) {
            console.error("Error fetching cart items:", error);
            setMessage("‚ùå Failed to fetch cart items.");
        }
    };

    // ‚úÖ Check Membership Status from User Controller
    const checkMembershipStatus = async () => {
        try {
            const response = await axios.get(`http://localhost:8080/users/${userId}`); // Call UserController
            setIsMember(response.data.isMember); // Extract 'isMember' field
        } catch (error) {
            console.error("Error checking membership status:", error);
        }
    };

    // ‚úÖ Update Cart Quantity
    const updateQuantity = async (cartId, newQuantity) => {
        if (newQuantity < 1) return; // Prevent negative or zero quantity
        try {
            await axios.put(`http://localhost:8080/api/cart/update/${cartId}`, null, {
                params: { quantity: newQuantity },
            });
            setMessage("‚úÖ Quantity updated!");
            fetchCartItems(); // Refresh cart
        } catch (error) {
            console.error("Error updating quantity:", error);
            setMessage("‚ùå Failed to update quantity.");
        }
    };

    // ‚úÖ Remove Item from Cart
    const removeFromCart = async (cartId) => {
        try {
            await axios.delete(`http://localhost:8080/api/cart/remove/${cartId}`);
            setMessage("üóë Item removed!");
            setCartItems(cartItems.filter((item) => item.cartId !== cartId)); // Remove item without API call
        } catch (error) {
            console.error("Error removing item:", error);
            setMessage("‚ùå Failed to remove item.");
        }
    };

    // ‚úÖ Calculate Total Price
    const getTotalPrice = () => {
        return cartItems.reduce((total, item) => total + item.product.price * item.quantity, 0);
    };

    // ‚úÖ Apply Discount if User is a Member
    const totalPrice = getTotalPrice();
    const discount = isMember ? totalPrice * 0.1 : 0;
    const finalPrice = totalPrice - discount;

    return (
        <div className="cart-container">
            <h2 className="cart-title">üõí Your Shopping Cart</h2>

            {/* ‚úÖ Display Alert Messages */}
            {message && <p className="alert-message">{message}</p>}

            {cartItems.length === 0 ? (
                <p className="empty-cart">Your cart is empty.</p>
            ) : (
                <>
                    <div className="cart-items">
                        {cartItems.map((item) => (
                            <div key={item.cartId} className="cart-item">
                                <img src={item.product.image} alt={item.product.name} className="cart-item-image" />
                                <div className="cart-item-details">
                                    <h6>{item.product.name} (ID: {item.product.productId})</h6>
                                    <p>Price: ‚Çπ{item.product.price}</p>

                                    {/* ‚úÖ Quantity Buttons */}
                                    <div className="cart-item-actions">
                                        <button onClick={() => updateQuantity(item.cartId, item.quantity - 1)}>-</button>
                                        <span>{item.quantity}</span>
                                        <button onClick={() => updateQuantity(item.cartId, item.quantity + 1)}>+</button>
                                    </div>

                                    <p className="item-total">Total: ‚Çπ{item.product.price * item.quantity}</p>

                                    {/* ‚úÖ Remove Button */}
                                    <button onClick={() => removeFromCart(item.cartId)} className="remove-btn">üóë Remove</button>
                                </div>
                            </div>
                        ))}
                    </div>

                    {/* ‚úÖ Billing Section */}
                    <div className="cart-summary">
                        <h3>Order Summary</h3>
                        <p>Subtotal ({cartItems.length} items): <strong>‚Çπ{totalPrice.toFixed(2)}</strong></p>
                        
                        {/* ‚úÖ Membership Status Display */}
                        <p className="membership-status">
                            Membership: {isMember ? "‚úÖ Member (10% Discount Applied)" : "‚ùå Not a Member"}
                        </p>

                        {isMember && (
                            <p className="discount-message">
                                üéâ Members get 10% OFF! <br />
                                <span className="strikethrough">‚Çπ{totalPrice.toFixed(2)}</span> ‚Üí <strong>‚Çπ{finalPrice.toFixed(2)}</strong> (Saved ‚Çπ{discount.toFixed(2)})
                            </p>
                        )}

                        <p className="final-price">Final Price: <strong>‚Çπ{finalPrice.toFixed(2)}</strong></p>
                        <button className="checkout-btn">Proceed to Buy</button>
                    </div>
                </>
            )}
        </div>
    );
};

export default Cart;
